INSERT INTO `wp_laminas` (`ID`, `ID_materia`, `ID_assunto`, `nome`, `slug`, `descricao`, `coloracao`, `aumento`, `imagem`, `detalhes`, `mascaras`, `b_del`) VALUES
(3, 1, 4, 'Esfregaço de sangue – eritrócitos e plaquetas', 'exemplo-1', 'Observam-se diversas hemácias (eritrócitos), células anucleadas que exibemcitoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativade formato bicôncavo. Notam-se também oito plaquetas, corpúsculos anucleados ebiconvexos que são fragmentos do citoplasma de megacariócitos, grandes células denúcleo multilobulado que emitem prolongamentos pelas fenestras dos capilaressinusóides para formação e liberação das plaquetas na corrente sanguínea.Diferentemente do que se observa neste preparado, os referidos corpúsculosapresentam-se geralmente em aglomerados. Uma discreta presença de plasmalevemente acidófilo é perceptível em quase todo o campo microscópico.', 'Romanowsky modificada', 1000, 99, '[{"id":1,"top":"25","left":"55","width":"15","height":"15","slug":"esfregaco-de-sangue-4"}]', '', 1),
(4, 1, 0, 'Esfregaço de sangue – eritrócitos e plaquetas	', 'exemplo-2', 'Aqui vao as @hemaceas e as @plaquetas ', 'Romanowsky modificada', 1000, 7, '', '', 1),
(5, 1, 2, 'Esfregaço de sangue – eritrócitos e plaquetas', 'esfregaco-de-sangue', 'Observam-se diversas @hemacias (eritrócitos), células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo. Notam-se também oito @plaquetas , corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea.Diferentemente do que se observa neste preparado, os referidos corpúsculos apresentam-se geralmente em aglomerados. Uma discreta presença de plasma levemente acidófilo é perceptível em quase todo o campo microscópico.', 'Romanowsky modificada', 1000, 7, '', '[{"id":1,"post":"42","title":"Hemácias","alias":"hemacias","description":"Algumas hemáceas sobrepostas e outras livres com sua região central com menos cor, devido ao seu formato e menor espessura no centro","color":"#1e73be"},{"id":2,"post":"43","title":"Plaquetas","alias":"plaquetas","description":"Plaquetas são como pedaços de algo maior, os megacariócitos, seu precursor","color":"#6410b7"}]', 0),
(8, 1, 2, 'Esfregaço de sangue - eritrócitos, plaquetas e eosinófilo', 'esfregaco-de-sangue-3', 'Observam-se diversas @hemacias  (eritrócitos), células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo. Destaque-se, no entanto, que o campo microscópico mostra muitas áreas de @hemacias  sobrepostas. Para a análise adequada das referidas células, é importante que sejam eleitas áreas do esfregaço de sangue com ausência de sobreposição entre elas. Notam-se também oito @plaquetas , corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea. Diferentemente do que se observa neste preparado, os referidos corpúsculos apresentam-se geralmente em aglomerados. Por fim, é notável a presença de um @eosinofilo , com grânulos específicos (secundários) acidófilos e núcleo bilobulado e excêntrico.', 'Romanowsky modificada', 1000, 16, '', '[{"id":1,"post":"44","title":"Eosinófilo","alias":"eosinofilo","description":"É notável a presença de um eosinófilo, com grânulos específicos (secundários) acidófilos e núcleo bilobulado e excêntrico","color":"#6a286d"},{"id":2,"post":"45","title":"Hemácias","alias":"hemacias","description":"Observam-se diversas hemácias (eritrócitos) sobrepostas, células anucleadas que exibem citoplasma fortemente acidófilo, devido á hemoglobina, com região central clara indicativa de formato bicôncavo. ","color":"#1fc1c6"},{"id":3,"post":"46","title":"Plaquetas","alias":"plaquetas","description":"Notam-se também oito plaquetas, corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea","color":"#601404"}]', 0),
(9, 1, 2, 'esfregaço de sangue - eritrócitos, plaquetas e linfócito ', 'esfregaco-de-sangue-4', 'Observam-se diversas @hemacias (eritrócitos), células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo. Destaque-se, no entanto, que o campo microscópico mostra muitas áreas de @hemacias sobrepostas. Para a análise adequada das referidas células, é importante que sejam eleitas áreas do esfregaço de sangue com ausência de sobreposição entre elas. Notam-se também oito @plaquetas , corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea. Diferentemente do que se observa neste preparado, os referidos corpúsculos apresentam-se geralmente em aglomerados. Uma discreta presença de plasma levemente acidófilo é perceptível em quase todo o campo microscópico. Ademais, observa-se a presença de um @linfocito , o menor dentre os leucócitos, com diâmetro comparável ao de uma hemácia, exibindo um núcleo esférico e intensamente basófilo que ocupa quase toda a célula, ficando uma pequena porção de citoplasma azurófilo desprovido de grânulos específicos (secundários).', 'Romanowsky modificada', 1000, 21, '', '[{"id":1,"post":"47","title":"Hemácias","alias":"hemacias","description":"Observam-se diversas hemácias (eritrócitos), células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo, sobrepostas.","color":"#0166bf"},{"id":2,"post":"48","title":"Plaquetas","alias":"plaquetas","description":"Notam-se também oito plaquetas, corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea","color":"#8d0bea"},{"id":3,"post":"49","title":"Linfócito","alias":"linfocito","description":"observa-se a presença de um linfócito, o menor dentre os leucócitos, com diâmetro comparável ao de uma hemácia, exibindo um núcleo esférico e intensamente basófilo que ocupa quase toda a célula, ficando uma pequena porção de citoplasma azurófilo desprovido de grânulos específicos (secundários)","color":"#4c0319"}]', 0),
(10, 1, 2, 'Esfregaço de sangue – eritrócitos, plaquetas e neutrófilo', 'esfregaco-de-sangue-5', 'Observam-se diversas @hemacias  (eritrócitos), células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo. Destaque-se, no entanto, que o campo microscópico mostra muitas áreas de @hemacias sobrepostas. Para a análise adequada das referidas células, é importante que sejam eleitas áreas do esfregaço de sangue com ausência de sobreposição entre elas. Notam-se também quatro @plaquetas , corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea. Diferentemente do que se observa neste preparado, os referidos corpúsculos apresentam-se geralmente em aglomerados. Uma discreta presença de plasma levemente acidófilo é perceptível em quase todo o campo microscópico. Um @neutrofilo também é perceptível, exibindo citoplasma com grânulos específicos (secundários) discretos e na cor salmon, e @nucleo  segmentado geralmente com 3 a 5 lóbulos unidos por finas pontes de cromatina.', 'Romanowsky modificada', 1000, 40, '', '[{"id":1,"post":"50","title":"Hemácias","alias":"hemacias","description":" células anucleadas que exibem citoplasma fortemente acidófilo, devido à hemoglobina, com região central clara indicativa de formato bicôncavo","color":"#2a6f9e"},{"id":2,"post":"51","title":"Plaquetas","alias":"plaquetas","description":"Notam-se também quatro plaquetas, corpúsculos anucleados e biconvexos que são fragmentos do citoplasma de megacariócitos, grandes células de núcleo multilobulado que emitem prolongamentos pelas fenestras dos capilares sinusóides para formação e liberação das plaquetas na corrente sanguínea. ","color":"#420282"},{"id":3,"post":"52","title":"Neutrófilo","alias":"neutrofilo","description":" exibindo citoplasma com grânulos específicos (secundários) discretos e na cor salmon","color":"#63001a"},{"id":4,"post":"53","title":"Núcleo ","alias":"nucleo ","description":"geralmente com 3 a 5 lóbulos unidos por finas pontes de cromatina","color":"#af3a36"}]', 0),
(11, 1, 2, 'Medula hematógena', 'medula-hematogena', 'Nota-se a medula hematógena preenchendo espaços intercomunicantes entre @trabeculas_osseas  do osso esponjoso desmineralizado. A medula hematógena caracteriza-se pela predominância de células em diferentes estágios de diferenciação na hematopoese, não distinguíveis nesta magnificação (células-tronco pluripotentes e multipotentes, unidades formadoras de colônias, hemocitoblastos e células já completamente diferenciadas, como megacariócito, hemácia e neutrófilo). Ademais, o mencionado tecido exibe células reticulares (fibroblastos estrelados, com diversos prolongamentos que se comunicam por junções comunicantes e produzem uma matriz extracelular com proteoglicanos, glicosaminoglicanos e muitas fibras reticulares não coradas por HE). Devido à riqueza em fibras reticulares, a medula hematógena, ativa na formação de células e elementos figurados do sangue, também é chamada de tecido reticular. Nela, notam-se também adipócitos diferenciados a partir de células reticulares ou de células mesenquimais indiferenciadas (mecanismo ainda não muito bem esclarecido). Capilares descontínuos sinusóides deste tecido recebem os elementos do sangue aí produzidos. As @trabeculas_osseas desmineralizadas mostram lacunas com corpos celulares de osteócitos pouco preservados e matriz orgânica acidófila devido à sua constituição de 95% de colágeno tipo I.', 'HE', 40, 29, '', '[{"id":2,"post":"54","title":"Trabéculas Ósseas","alias":"trabeculas_osseas","description":"As trabéculas ósseas desmineralizadas mostram lacunas com corpos celulares de osteócitos pouco preservados e matriz orgânica acidófila devido à sua constituição de 95% de colágeno tipo I","color":"#60baac"}]', 0),
(12, 1, 2, 'Medula hematógena - megacariócito', 'medula-hematogena-megacariocito', 'Observa-se a medula hematógena com células em diferentes estágios de diferenciação na hematopoese. Dentre elas, apenas uma pode ser identificada de modo confiável: o @megacariocito , grande, com núcleo multilobulado, cujos prolongamentos que penetram pelas fenestras dos capilares sinusóides aí presentes são fragmentados para formação das plaquetas.', 'HE', 400, 30, '', '[{"id":1,"post":"55","title":"Megacariócito","alias":"megacariocito","description":"grande, com núcleo multilobulado, cujas prolongamentos que penetram pelas fenestras dos capilares sinusóides aí presentes são fragmentos para formação das plaquetas.","color":"#7c1f87"}]', 0),
(13, 1, 2, 'Medula amarela', 'medula-amarela', 'Nota-se a medula amarela preenchendo espaços intercomunicantes entre @trabeculas_osseas do osso esponjoso desmineralizado. A medula amarela caracteriza-se pela predominância @adipocitos e resquícios de medula hematógena associados ao predominante @tecido_adiposo unilocular. A medula amarela pode ser revertida à medula hematógena quando há necessidade de hematopoese aumentada, como após grandes hemorragias. As trabéculas ósseas desmineralizadas mostram lacunas com corpos celulares de osteócitos pouco preservados e matriz orgânica acidófila devido à sua constituição de 95% de colágeno tipo I.', 'HE', 40, 31, '', '[{"id":1,"post":"56","title":"Adipócitos","alias":"adipocitos","description":"A medula amarela caracteriza-se pela predominância adipócitos e","color":"#ffe607"},{"id":2,"post":"57","title":"Medula hematogênica","alias":"medula_hematogenica","description":"Parte ainda de medula hmatogênica com os precussores celular","color":"#774d1c"},{"id":3,"post":"58","title":"Tecido Adiposo Unilocular","alias":"tecido_adiposo","description":"A medula amarela caracteriza-se pela predominância adipócitos e resquícios de medula hematógena associados ao predominante tecido adiposo unilocular.","color":"#d1a300"},{"id":4,"post":"59","title":"Trabéculas Ósseas","alias":"trabeculas_osseas","description":"esmineralizadas mostram lacunas com corpos celulares de osteócitos pouco preservados e matriz orgânica acidófila devido à sua constituição de 95% de colágeno tipo I.","color":"#3f7d7f"}]', 0),
(14, 1, 3, 'Coração - miocárdio ventricular e fibra de Purkinje', 'coracao-miocardio', 'O preparado permite a observação apenas do @miocardio_ventricular e @fibra_de_purkinje (segmento próximo à sua extremidade distal, que penetra como garra no miocárdio ventricular). As células musculares cardíacas contráteis exibem forte acidofilia citoplasmática devido à riqueza em mitocôndrias e miofibrilas, estas constituídas por proteínas filamentosas contráteis básicas. @discos_intercalares característicos do músculo cardíaco também são perceptíveis. As grandes células musculares cardíacas modificadas que constituem as fibras de Purkinje mostram fraca acidofilia citoplasmática devido à expressiva quantidade de glicogênio e maior acidofilia na periferia citoplasmática, onde ocorre maior concentração de miofibrilas.', 'HE', 200, 32, '', '[{"id":1,"post":"62","title":"Miocárdio ventricular","alias":"miocardio_ventricular","description":"","color":"#40d679"},{"id":2,"post":"63","title":"Fibra de Purkinje","alias":"fibra_de_purkinje","description":"segmento próximo à sua extremidade distal, que penetra como garra no miocárdio ventricular","color":"#175863"},{"id":3,"post":"64","title":"Discos intercalares","alias":"discos_intercalares","description":"característicos do músculo cardíaco também são perceptíveis","color":"#9e7a3d"}]', 0),
(15, 1, 3, 'Miocárdio ventricular e epicárdio', 'miocardio-ventricular-e-epicardio', 'Observam-se @miocardio e @epicardio . O miocárdio é constituído basicamente por células musculares cardíacas contráteis com forte acidofilia citoplasmática, dispostas em várias direções. O epicárdio, também conhecido como o pericárdio visceral, é a camada mais externa da parede do coração, composta por tecido conjuntivo denso fibroelástico e tecido conjuntivo frouxo contendo vasos, feixes de fibras nervosas e áreas de tecido adiposo unilocular.', 'HE', 100, 33, '', '[{"id":1,"post":"65","title":"Miocárdio","alias":"miocardio","description":"é constituído basicamente por células musculares cardíacas contráteis com forte acidofilia citoplasmática, dispostas em várias direções.","color":"#100059"},{"id":2,"post":"66","title":"Epicárdio","alias":"epicardio","description":" também conhecido como o pericárdio visceral, é a camada mais externa da parede do coração, composta por tecido conjuntivo denso fibroelástico e tecido conjuntivo frouxo contendo vasos","color":"#5e80f2"}]', 0),
(16, 1, 3, 'Artéria elástica - túnicas íntima e média', 'arteria-elastica-tunicas-intima-e-media', 'Neste corte, parte da parede da artéria aorta mostra revestimento interno de @endotelio (epitélio simples pavimentoso) de difícil visualização apoiado em uma camada @subendotelial de tecido conjuntivo frouxo. A @tunica_media bem desenvolvida é constituída principalmente por membranas (lâminas) elásticas fenestradas concêntricas que exibem moderada acidofilia. Não é possível identificar a membrana limitante elástica interna.', 'HE', 40, 34, '', '[{"id":1,"post":"67","title":"Túnica média","alias":"tunica_media","description":"onstituída principalmente por membranas (lâminas) elásticas fenestradas concêntricas que exibem moderada acidofilia.","color":"#3f3f12"},{"id":2,"post":"68","title":"endotélio","alias":"endotelio","description":"","color":"#100744"},{"id":3,"post":"69","title":"subendotelial","alias":"subendotelial","description":"","color":"#ad3e70"}]', 0),
(17, 1, 3, 'Artéria elástica - túnicas íntima e média 100x', 'arteria-elastica-tunicas', '2Neste corte, parte da parede da artéria aorta mostra revestimento interno de endotelio (epitélio simples pavimentoso) de difícil visualização apoiado em uma camada @subendotelial de tecido conjuntivo frouxo. A @tunica_media  bem desenvolvida é constituída principalmente por membranas (lâminas) elásticas fenestradas concêntricas que exibem moderada acidofilia. Não é possível identificar a membrana limitante elástica interna. Os núcleos perceptíveis na túnica média são de células musculares lisas.', 'HE', 100, 35, '', '[{"id":1,"post":"70","title":"endotélio","alias":"endotelio","description":"","color":"#d15173"},{"id":2,"post":"71","title":"subendotelial","alias":"subendotelial","description":"","color":"#dd7356"},{"id":3,"post":"72","title":"Túnica média","alias":"tunica_media","description":"bem desenvolvida é constituída principalmente por membranas (lâminas) elásticas fenestradas concêntricas que exibem moderada acidofilia.","color":"#46697a"}]', 0),
(18, 1, 3, 'Artéria elástica túnica média e adventícia ', 'arteria-elastica-5', 'Neste corte, parte da parede da artéria aorta mostra túnicas @media e @adventicia . A túnica adventícia é constituída por tecido conjuntivo frouxo, contendo fibras elásticas aqui não coradas e colágenas acidófilas, com presença de @vasa_vasorum e @nervi_vasorum . Não é possível identificar a membrana limitante elástica externa.', 'HE', 40, 36, '', '[{"id":1,"post":"73","title":"Túnica Média","alias":"media","description":"","color":"#0c3000"},{"id":2,"post":"74","title":"Túnica Adventícia","alias":"adventicia","description":"constituída por tecido conjuntivo frouxo, contendo fibras elásticas aqui não coradas e colágenas acidófilas","color":"#62db55"},{"id":3,"post":"75","title":"Vasa vasorum","alias":"vasa_vasorum","description":"","color":"#b70030"},{"id":4,"post":"76","title":"nervi vasorum","alias":"nervi_vasorum","description":"","color":"#c3ff00"}]', 0),
(19, 1, 3, 'Artéria muscular - túnicas íntima e média', 'arteria-muscular-1', 'A @tunica_intima exibe @endotelio apoiado em uma delgada faixa de @tecido_conjuntivo_frouxo . Separando as túnicas íntima e média, nota-se a @membrana_limitante_elastica_interna bem definida, acidófila e com aspecto ondulado. A @tunica_media apresenta uma camada espessa de músculo liso.', 'HE', 40, 37, '', '[{"id":3,"post":"79","title":"túnica íntima","alias":"tunica_intima","description":"","color":"#82aa6b"},{"id":4,"post":"80","title":"endotélio","alias":"endotelio","description":"","color":"#9b2e22"},{"id":5,"post":"81","title":"tecido conjuntivo frouxo","alias":"tecido_conjuntivo_frouxo","description":"","color":"#0d004f"},{"id":6,"post":"82","title":"membrana limitante elastica interna","alias":"membrana_limitante_elastica_interna","description":"","color":"#d6409c"},{"id":7,"post":"83","title":"túnica media","alias":"tunica_media","description":"","color":"#253d00"}]', 0),
(20, 1, 3, 'Artéria muscular - túnicas íntima e média 200x', 'arteria-muscular-2', 'A @tunica_intima exibe @endotelio apoiado em uma delgada faixa de @tecido_conjuntivo frouxo. Separando as túnicas íntima e média, nota-se a @membrana_limitante_elastica_interna bem definida, acidófila e com aspecto ondulado. A @tunica_media apresenta uma camada espessa de músculo liso.', 'HE', 200, 38, '', '[{"id":3,"post":"84","title":"endotélio","alias":"endotelio","description":"","color":"#7a2f6f"},{"id":4,"post":"85","title":"membrana limitante elastica interna","alias":"membrana_limitante_elastica_interna","description":"","color":"#5a53d6"},{"id":5,"post":"86","title":"tecido conjuntivo","alias":"tecido_conjuntivo","description":"","color":"#348741"},{"id":6,"post":"87","title":"túnica íntima","alias":"tunica_intima","description":"","color":"#968b37"},{"id":7,"post":"88","title":"túnica média","alias":"tunica_media","description":"","color":"#9add56"}]', 0),
(21, 1, 3, 'Artéria muscular - túnicas média e advertícia ', 'arteria-muscular-3', 'Separando as túnicas @media e @adventicia , há uma membrana limitante elástica externa que não está visível neste corte. A túnica adventícia mostra de tecido conjuntivo frouxo, com fibras colágenas acidófilas e elásticas não coradas, @vasa_vasorum e @nervi_vasorum .', 'HE', 40, 39, '', '[{"id":1,"post":"89","title":"nervi vasorum","alias":"nervi_vasorum","description":"","color":"#63fffc"},{"id":2,"post":"90","title":"Túnica média","alias":"media","description":"","color":"#aeb245"},{"id":3,"post":"91","title":"túnicas adventícia","alias":"adventicia","description":"","color":"#365387"},{"id":4,"post":"92","title":"vasa vasorum","alias":"vasa_vasorum","description":"","color":"#ef003f"}]', 0),
(22, 1, 4, 'Traqueia - mucosa respiratória, submucosa e placa de cartilagem ', 'traqueia-mucosa', 'Nota-se o @epitelio_pseudoestratificado_ciliado (respiratório) apoiado em um tecido conjuntivo frouxo e bem vascularizado ( @lamina_propria ). A submucosa mostra tecido conjuntivo denso não modelado e muitas @glandulas_seromucosas . Algumas células @adiposas_uniloculares são também observadas na @submucosa , que é contínua com o @pericondrio do anel de @cartilagem_hialina , observado em parte no canto inferior direito do corte em análise.', 'HE', 40, 93, '', '[{"id":1,"post":"110","title":"cartilagem hialina","alias":"cartilagem_hialina","description":"","color":"#dd9933"},{"id":2,"post":"111","title":"adiposas uniloculares","alias":"adiposas_uniloculares","description":"","color":"#070405"},{"id":3,"post":"112","title":"epitélio pseudoestratificado ciliado","alias":"epitelio_pseudoestratificado_ciliado","description":"","color":"#eded82"},{"id":4,"post":"113","title":"glândulas seromucosas ","alias":"glandulas_seromucosas","description":"","color":"#325119"},{"id":5,"post":"114","title":"Lâmina própria","alias":"lamina_propria","description":"","color":"#d8546a"},{"id":6,"post":"115","title":"pericondrio","alias":"pericondrio","description":"","color":"#4fbac6"},{"id":7,"post":"116","title":"submucosa","alias":"submucosa","description":"","color":"#271d4c"}]', 0),
(23, 1, 4, 'Traqueia - epitélio respiratório', 'traqueia-epitelio', 'Este corte evidencia em grande magnitude o @epitelio_respiratorio apoiado sobre a @lamina_propria . Notam-se no epitélio as três células nele mais numerosas: @basais , @ciliadas e @caliciformes . A lâmina própria caracteriza-se por um tecido conjuntivo frouxo com diversos vasos sanguíneos da microcirculação, no qual é notável também a presença de fibroblastos e células do sistema imunológico, especialmente linfócitos e plasmócitos. ', 'HE', 200, 94, '[]', '[{"id":1,"post":"117","title":"Células basais","alias":"basais","description":"","color":"#add802"},{"id":2,"post":"118","title":"Células caliciformes","alias":"caliciforme","description":"","color":"#5d7def"},{"id":3,"post":"119","title":"Células ciliadas","alias":"ciliadas","description":"","color":"#d60039"},{"id":4,"post":"120","title":"Epitélio respiratório","alias":"epitelio_respiratorio","description":"","color":"#e28258"},{"id":5,"post":"121","title":"Lâmina própria","alias":"lamina_propria","description":"própria caracteriza-se por um tecido conjuntivo frouxo com diversos vasos sanguíneos da microcirculação, no qual é notável também a presença de fibroblastos e células do sistema imunológico, especialmente linfócitos e plasmócitos.","color":"#2a5d72"}]', 0),
(24, 1, 4, 'Brônquio intrapulmonar em corte transversal - epitélio respiratório, lâmina própria, músculo liso, s', 'bronquio-intrapulmonar', 'Observa-se o @epitelio_pseudoestratificado_ciliado (respiratório) moderadamente preservado pela técnica de processamento histológico empregada. Este epitélio apoia-se uma lâmina própria bem vascularizada que exibe fibroblastos e linfócitos. Uma camada descontínua de @musculo_liso separa a lâmina própria da submucosa, aqui formada por tecido que varia de frouxo a denso não modelado em algumas áreas. A submucosa sustenta glândulas seromucosas de difícil identificação neste aumento devido ao expressivo número de linfócitos que formam nódulos característicos do @tecido_linfoide_associado_aos_bronquios (BALT, do inglês bronchus-associated lymphoid tissue). A submucosa é unida ao pericôndrio das placas irregulares de @cartilagem_hialina da camada adventícia. ', 'HE', 40, 95, '', '[{"id":1,"post":"122","title":"cartilagem hialina","alias":"cartilagem_hialina","description":"","color":"#c760f7"},{"id":2,"post":"123","title":"Epitélio Pseudoestratificado Ciliado ","alias":"epitelio_pseudoestratificado_ciliado","description":"","color":"#d6784d"},{"id":3,"post":"124","title":"Músculo Liso","alias":"musculo_liso","description":"","color":"#dee059"},{"id":4,"post":"125","title":"Tecido Linfóide associado aos brônquios","alias":"tecido_linfoide_associado_aos_bronquios","description":"","color":"#bada55"}]', 0),
(25, 1, 4, 'Parede de brônquio intrapulmonar', 'parede-de-bronquio', 'Notam-se @epitelio_respiratorio de revestimento interno do brônquio, @lamina_propria , @musculo_liso descontínuo no corte (devido à sua organização em espiral), @submucosa sustentando @glandulas_seromucosas e @nodulo_linfoide , e placas irregulares de @cartilagem_hialina .  ', 'HE', 100, 97, '', '[{"id":1,"post":"126","title":"Epitélio respiratório","alias":"epitelio_respiratorio","description":"","color":"#dd6a6a"},{"id":2,"post":"127","title":"Lâmina própria","alias":"lamina_propria","description":"","color":"#227513"},{"id":3,"post":"128","title":"Músculo Liso","alias":"musculo_liso","description":"","color":"#c44ca6"},{"id":4,"post":"129","title":"Submucosa","alias":"submucosa","description":"","color":"#8c9339"},{"id":5,"post":"130","title":"Glândulas seromucosas","alias":"glandulas_seromucosas","description":"","color":"#5a97e8"},{"id":6,"post":"131","title":"nódulo linfóide","alias":"nodulo_linfoide","description":"","color":"#963a68"},{"id":7,"post":"132","title":"cartilagem hialina","alias":"cartilagem_hialina","description":"","color":"#5a2868"}]', 0),
(26, 1, 4, 'Pulmão - alvéolos e bronquílo', 'pulmao-alveolos', 'Observa-se o parênquima pulmonar normal com @alveolos íntegros e algumas áreas com maior quantidade de @tecido_conjuntivo_fibroelastico sustentando vasos sanguíneos maiores. Estas áreas com maior quantidade de tecido conjuntivo delimitam os segmentos broncopulmonares, que são limites importantes a serem considerados por cirurgiões torácicos. Os @espacos_aereos são delimitados por @septos_interalveolares delgados formados por tecido conjuntivo frouxo bem vascularizado e revestido em ambos os lados por pneumócitos tipo I, em uma microarquitetura com aspecto de colmeia. Os capilares sanguíneos presentes nos septos interalveolares forma a maior rede capilar do organismo humano responsável pelas trocas gasosas que viabilizam a oxigenação do sangue e eliminação do gás carbônico, caracterizando a respiração externa. O aspecto de colmeia é claramente perdido em processos patológicos como enfisema pulmonar decorrente do tabagismo, havendo um expressivo aumento do tamanho dos espaços aéreos em decorrência da degradação dos septos interalveolares, o que compromete em grande medida a respiração externa. ', 'HE', 40, 96, '[]', '[{"id":1,"post":"133","title":"Alvéolos","alias":"alveolos","description":"","color":"#49a5c6"},{"id":2,"post":"134","title":"Tecido Conjuntivo Fibroelástico","alias":"tecido_conjuntivo_fibroelastico","description":"","color":"#af7a44"},{"id":3,"post":"135","title":"Espaços Aéreos","alias":"espacos_aereos","description":"","color":"#726f29"},{"id":4,"post":"136","title":"Septos Interalveolares","alias":"septos_interalveolares","description":"","color":"#3d9e48"}]', 0),
(27, 1, 4, 'Pulmão - alvéolos e bronquíolos', 'pulmao-alveolos2', 'Os @bronquiolos apresentam-se revestidos internamente por epitélio simples que pode variar de cilíndrico a cúbico, com células caliciformes que podem aparecer em pequeno número ou mesmo estarem ausentes. O epitélio está apoiado sobre uma delgada faixa de lâmina própria (não visualizada) adjacente a uma camada contínua de @musculo_liso circularmente orientado e bem desenvolvido. Notam-se aqui também @arterias_musculares e @alveolos . ', 'HE', 40, 98, '', '[{"id":1,"post":"143","title":"Bronquíolos","alias":"bronquiolos","description":"apresentam-se revestidos internamente por epitélio simples que pode variar de cilíndrico a cúbico, com células caliciformes que podem aparecer em pequeno número ou mesmo estarem ausentes","color":"#71b245"},{"id":2,"post":"144","title":"Músculo liso","alias":"musculo_liso","description":"","color":"#3ea07f"},{"id":3,"post":"145","title":"Artérias musculares","alias":"arterias_musculares","description":"","color":"#4261aa"},{"id":4,"post":"146","title":"Alvéolos","alias":"alveolos","description":"","color":"#d653cb"}]', 0),
(28, 1, 4, 'Alvéolos pulmonares - septos interalveolares e espaços aéreos', 'Alveolos-pulmonares', ' @espacos_aereos delimitados por @septos_interalveolares delgados ftecido_conjuntivo frouxo bem vascularizado e revestido em ambos os lados por @pneumocitos_tipo_I . Estas células são pavimentosas e revestem aproximadamente 90% da superfície interna dos alvéolos. O @pneumocitos_tipo_II , células globosas de núcleo esférico, localizam-se geralmente no encontro de dois septos interalveolares, revestem em torno de 10% da superfície interna dos alvéolos e produzem a substância surfactante pulmonar, que diminui a tensão superficial dos alvéolos evitando o colabamento de suas paredes na expiração. Neste corte, notamos ainda uma área com maior quantidade de tecido conjuntivo entre os alvéolos, exibindo um @macrofago e sustentando uma pequena @veia , na qual estão bem preservados @plasma sanguíneo e @hemacias . ', 'HE', 400, 99, '', '[{"id":1,"post":"142","title":"Espaços aéreos","alias":"espacos_aereos","description":"","color":"#fcf262"},{"id":2,"post":"147","title":"Septos interalveolares","alias":"septos_interalveolares","description":"","color":"#ea002e"},{"id":3,"post":"148","title":"Tecido conjuntivo","alias":"tecido_conjuntivo","description":"","color":"#63ff66"},{"id":4,"post":"149","title":"Pneumócitos tipo I","alias":"pneumocitos_tipo_I","description":"","color":"#62fca2"},{"id":5,"post":"150","title":"Pneumócitos tipo II","alias":"pneumocitos_tipo_II","description":"","color":"#50ce63"},{"id":6,"post":"151","title":"Macrófago","alias":"macrofago","description":"","color":"#47b781"},{"id":7,"post":"152","title":"Veia","alias":"veia","description":"","color":"#007a2a"},{"id":8,"post":"153","title":"Plasma","alias":"plasma","description":"","color":"#274900"},{"id":9,"post":"154","title":"Hemácias","alias":"hemacias","description":"","color":"#2f3513"}]', 0),
(29, 1, 4, 'Alvéolos pulmonares - macrófago alveolar ou célula de poeira ', 'Alveolos-pulmonares2', 'Observam-se espaços aéreos e septos interalveolares bem vascularizados, núcleos de pneumócitos tipos @I e @II , e um @macrofago_alveolar no centro do campo microscópico. ', 'HE', 400, 100, '', '[{"id":1,"post":"139","title":"Alvéolos","alias":"alveolos","description":"","color":"#51311f"},{"id":2,"post":"140","title":"Macrófago Alveolar","alias":"macrofago_alveolar","description":"","color":"#9b3461"},{"id":3,"post":"141","title":"Pneumócitos I","alias":"I","description":"","color":"#50cece"},{"id":4,"post":"194","title":"Pneumócitos II","alias":"II","description":"","color":"#46b58a"}]', 0),
(30, 1, 4, 'Alvéolos e pleura visceral', 'alveolos-pleura', 'Externamente, o pulmão mostra uma faixa mais robusta de tecido conjuntivo fibroelástico revestida por um mesotélio (epitélio simples pavimentoso), conhecida como @pleura_visceral . Esta é contínua com a pleura parietal, que reveste a cavidade torácica e seus mesotélios delimitam a cavidade pleural. ', 'HE', 100, 193, '', '[{"id":1,"post":"137","title":"Alvéolos","alias":"alveolos","description":"","color":"#3c9b98"},{"id":2,"post":"138","title":"Pleura visceral","alias":"pleura_visceral","description":"","color":"#7ee057"}]', 0),
(31, 1, 1, 'Mucosa do ureter', 'mucosa-do-ureter', 'Nota-se aqui pregueado o @epitelio_de_transicao que reveste internamente o ureter e cujas células superficiais modificam sua morfologia conforme a distensão do órgão. Abaixo do epitélio, há uma extensa @lamina_propria bem vascularizada. As túnicas muscular e adventícia não estão evidentes neste campo microscópico. ', 'HE', 100, 101, '', '[{"id":1,"post":"155","title":"Epitélio de transição","alias":"epitelio_de_transicao","description":"que reveste internamente o ureter e cujas células superficiais modificam sua morfologia conforme a distensão do órgão","color":"#5ee559"},{"id":2,"post":"156","title":"Lâmina própria","alias":"lamina_propria","description":"","color":"#a7d804"}]', 0),
(32, 1, 1, 'Camada muscular da parede do ureter', 'camada-muscular-da-parede-do-ureter', 'Observa-se a camada muscular da parede do ureter, formada por camadas de músculo liso dispostas em espiral e dessa forma exibindo feixes de células musculares em cortes @longitudinal e @transversal unidos por @tecido_conjuntivo que varia de frouxo a denso. Circundando a parede muscular, há uma adventícia constituída de tecido conjuntivo frouxo contendo vasos, nervos e células adiposas, aqui não visualizada. ', 'HE', 100, 102, '', '[{"id":1,"post":"157","title":"Feixes de células musculares em corte longitudinal","alias":"longitudinal","description":"","color":"#515e10"},{"id":2,"post":"158","title":"feixes de células musculares em corte transversal","alias":"transversal","description":"","color":"#08513f"},{"id":3,"post":"159","title":"Tecido conjuntivo","alias":"tecido_conjuntivo","description":"","color":"#d10202"}]', 0),
(33, 1, 1, 'Córtex renal', 'cortex-renal', 'O rim é um órgão com forma de grão de feijão, sendo constituído por uma cápsula de tecido conjuntivo e pelas zonas cortical e medular. Aqui, em pequeno aumento, notamos um corte da zona cortical do rim exibindo quatro @corpusculos_renais e @tubulos_contorcidos proximais e distais (de difícil distinção neste aumento), sustentados por um escasso tecido conjuntivo frouxo e bem vascularizado, o interstício renal. ', 'HE', 40, 103, '', '[{"id":1,"post":"160","title":"Corpúsculos renais","alias":"corpusculos_renais","description":"","color":"#233500"},{"id":2,"post":"161","title":"Túbulos contorcidos","alias":"tubulos_contorcidos","description":"","color":"#3d2500"}]', 0),
(34, 1, 1, 'Córtex renal - corpúsculo renal, túbulos contorcidos proximais e distais', 'cortex-renal-corpusculo-renal', 'Notam-se três @corpusculos_renais (dois deles parcialmente seccionados na extremidade direita do campo microscópico) e túbulos contorcidos @proximais e @distais . Entre as mencionadas estruturas, é possível identificar o @intersticio_renal bem vascularizado. O corpúsculo renal é formado pelo glomérulo (rede capilar entre duas arteríolas, uma aferente e outra eferente) envolvido pela cápsula de Bowman, que exibe um @folheto_parietal (formado por um epitélio simples pavimentoso que conduz o filtrado glomerular ao túbulo contorcido proximal) e um @folheto_visceral (constituído por podócitos cujos prolongamentos secundários apoiam-se na membrana glomerular, uma barreira de filtração constituída por uma lâmina basal espessa entre as células endoteliais glomerulares e os podócitos). Entre os podócitos e as células endoteliais, estão ainda presentes células mesangiais intraglomerulares produtoras de uma matriz extracelular de sustentação e contráteis em resposta à angiotensina II (registre-se aqui que o fator natriurético atrial estimula o relaxamento delas). Entre o folheto parietal e os podócitos da cápsula de Bowman nota-se o @espaco_capsular . Apesar de observarmos claramente as hemácias presentes nos capilares, não distinguimos bem neste aumento os núcleos de células endoteliais, podócitos e células mesangiais. O @tubulo_contorcido_proximal apresenta epitélio cúbico simples, com células grandes e intensamente acidófilas no citoplasma, enquanto que o @tubulo_contorcido_distal apresenta células menores e com citoplasma menos acidófilo. Nas proximidades do corpúsculo renal, o túbulo contorcido distal apresenta um segmento da sua parede modificado, com células cilíndricas exibindo núcleos mais corados e próximos uns dos outros, a @macula_densa . ', 'HE', 100, 104, '', '[{"id":1,"post":"162","title":"Corpúsculos renais","alias":"corpusculos_renais","description":"","color":"#ed003f"},{"id":2,"post":"163","title":"Túbulos contorcidos proximais","alias":"proximais","description":"","color":"#90ff00"},{"id":3,"post":"164","title":"Túbulo contorcidos distais","alias":"distais","description":"","color":"#edd500"},{"id":4,"post":"165","title":"interstício renal","alias":"intersticio_renal","description":"","color":"#1f263f"},{"id":5,"post":"166","title":"folheto parietal","alias":"folheto_parietal","description":"","color":"#0a0000"},{"id":6,"post":"167","title":"folheto visceral","alias":"folheto_visceral","description":"","color":"#44001a"},{"id":7,"post":"168","title":"espaço capsular","alias":"espaco_capsular","description":"","color":"#0e3e3f"},{"id":8,"post":"169","title":"Túbulo Contorcido Proximal","alias":"tubulo_contorcido_proximal","description":"","color":"#0066bf"},{"id":9,"post":"170","title":"Túbulo Contorcido Distal","alias":"tubulo_contorcido_distal","description":"","color":"#7100e2"},{"id":10,"post":"171","title":"Mácula densa","alias":"macula_densa","description":"","color":"#dd9323"}]', 0),
(35, 1, 1, 'corpúsculo renal, túbulos contorcidos proximais, túbulo contorcido distal e aparelho justaglomerular', 'corpusculo-renal', 'Observam-se um @corpusculo_renal e túbulos contorcidos proximais e distais. Entre as mencionadas estruturas, é possível identificar o interstício renal bem vascularizado. Identificam-se as hemácias dentro dos capilares glomerulares e também dentro dos capilares peritubulares. São facilmente perceptíveis o @folheto_parietal da @capsula_de_Bowman e o @espaco_capsular . Não são bem distinguíveis também aqui os núcleos células endoteliais, podócitos e células mesangiais do corpúsculo renal. O @tubulo_contorcido_proximal apresenta epitélio cúbico simples, com células grandes e intensamente acidófilas no citoplasma, enquanto que o @tubulo_contorcido_distal apresenta células menores e com citoplasma menos acidófilo. Nas proximidades do corpúsculo renal, o túbulo contorcido distal apresenta um segmento da sua parede modificado, com células cilíndricas exibindo núcleos mais corados e próximos uns dos outros, a @macula_densa . Neste e em outros cortes de córtex renal, notam-se mais túbulos contorcidos proximais do que distais no mesmo campo microscópico. Isto se deve à extensão do proximal, que é bem maior do que a do distal.  ', 'HE', 200, 105, '', '[{"id":1,"post":"172","title":"Corpúsculo renal","alias":"corpusculo_renal","description":"","color":"#898989"},{"id":2,"post":"173","title":"capsula de Bowman","alias":"capsula_de_Bowman","description":"","color":"#721ebc"},{"id":3,"post":"174","title":"espaço capsular","alias":"espaco_capsular","description":"","color":"#eded00"},{"id":4,"post":"175","title":"folheto parietal","alias":"folheto_parietal","description":"","color":"#280f18"},{"id":5,"post":"176","title":"folheto visceral","alias":"folheto_visceral","description":"","color":"#000660"},{"id":6,"post":"177","title":"macula densa","alias":"macula_densa","description":"","color":"#8c6436"},{"id":7,"post":"178","title":"Túbulo Contorcido Distal","alias":"tubulo_contorcido_distal","description":"","color":"#871b37"},{"id":8,"post":"179","title":"Túbulo Contorcido Proximal","alias":"tubulo_contorcido_proximal","description":"","color":"#006b50"}]', 0),
(36, 1, 1, 'Aparelho justaglomerular, túbulos contorcidos proximais e distais, e arteríola aferente', 'aparelho-justaglomerular', 'Notam-se @intersticio_renal ,@capilares_peritubulares , túbulos contorcidos @proximais e @distais , e parte do aparelho justaglomerular (aqui visualizam-se @macula_densa e @arteriola_aferente ).  ', 'HE', 400, 106, '', '[{"id":1,"post":"180","title":"Interstício real","alias":"intersticio_renal","description":"","color":"#8e3784"},{"id":2,"post":"181","title":"Capilares Peritubulares","alias":"capilares_peritubulares","description":"","color":"#027c45"},{"id":3,"post":"182","title":"túbulos contorcidos proximais ","alias":"proximais","description":"","color":"#4b54d1"},{"id":4,"post":"183","title":"túbulos contorcidos distais","alias":"distais","description":"","color":"#ba48a5"},{"id":5,"post":"184","title":"Mácula densa","alias":"macula_densa","description":"","color":"#604a1f"},{"id":6,"post":"185","title":"arteríola aferente","alias":"arteriola_aferente","description":"","color":"#328189"}]', 0),
(37, 1, 1, 'Medula renal - túbulos coletores, alça de Henle e vasos retos', 'medula-renal', 'Medula renal em corte longitudinal, onde são observados a @alca_de_Henle (porção delgada), @tubulos_coletores , interstício renal e @vasos_retos . A porção delgada da alça de Henle pode ser facilmente identificada por apresentar um epitélio pavimentoso simples e não exibir hemácias em sua luz, enquanto que a porção espessa da alça e os túbulos coletores são de difícil diferenciação, já que ambos apresentam epitélio cúbico simples.', 'HE', 200, 107, '', '[{"id":1,"post":"186","title":"alça de Henle","alias":"alca_de_Henle","description":"","color":"#35235b"},{"id":2,"post":"187","title":"Túbulos coletores","alias":"tubulos_coletores","description":"","color":"#406b29"},{"id":3,"post":"188","title":"Vasos Retos","alias":"vasos_retos","description":"","color":"#99813b"}]', 0),
(38, 1, 1, 'Medula renal - túbulos coletores, alça de Henle e vasos retos', 'medula-renal2', 'Medula renal em corte transversal, onde são observados a @alca_de_Henle (porções delgada e espessa), @tubulos_coletores , @intersticio_renal e @vasos_retos . A porção delgada da alça de Henle pode ser facilmente identificada por apresentar um epitélio pavimentoso simples e não exibir hemácias em sua luz, enquanto que a porção espessa da alça e os túbulos coletores são de difícil diferenciação, já que ambos apresentam epitélio cúbico simples.', 'HE', 200, 108, '', '[{"id":1,"post":"189","title":"alça de Henle","alias":"alca_de_Henle","description":"","color":"#3c789b"},{"id":2,"post":"190","title":"Túbulos coletores","alias":"tubulos_coletores","description":"","color":"#8c365b"},{"id":3,"post":"191","title":"interstício renal","alias":"intersticio_renal","description":"","color":"#3e772e"},{"id":4,"post":"192","title":"vasos retos","alias":"vasos_retos","description":"","color":"#e0c757"}]', 0),
(39, 2, 5, 'Estômago - mucosa e pequena parte da submucosa', 'estamago', 'Observa-se toda a espessura da mucosa gástrica, incluindo a muscular da mucosa e uma pequena porção da submucosa. O epitélio cilíndrico simples de revestimento interno do estômago, que é unido ao epitélio das fossetas gástricas, não foi preservado. Notam-se fossetas e glândulas gástricas características das regiões de corpo e fundo do estômago, envolvidas por uma lâmina própria (córion) de tecido conjuntivo frouxo que se estende até a muscular da mucosa. As fossetas exibem principalmente células mucosas (não muito perceptíveis neste aumento) e conduzem o produto de secreção das glândulas gástricas (suco gástrico) em direção ao lúmen do estômago, além de produzirem, juntamente com o epitélio cilíndrico simples de revestimento interno do órgão, o muco visível alcalino que protege a mucosa gástrica da ação do suco gástrico. ', 'HE', 40, 195, '', '', 0),
(40, 2, 5, 'Mucosa gástrica – fossetas e glândulas gástricas', 'mucosa-gastrica', 'O epitélio cilíndrico simples de revestimento interno do estômago não foi preservado. Notam-se fossetas e glândulas gástricas envolvidas por uma lâmina própria (córion) de tecido conjuntivo frouxo. É possível identificar também neste aumento células musculares lisas (provenientes da muscular da mucosa) na lâmina própria entre as glândulas gástricas. A contração das células musculares lisas da mucosa gástrica facilita a liberação do suco gástrico. Nas glândulas gástricas, dois tipos celulares estão bem evidentes: células parietais e células principais. As células principais (produtoras de pepsinogênio) são menores, apresentando um formato piramidal, núcleos basalmente localizados e acentuada basofilia citoplasmática. Já as células parietais (produtoras de HCl e fator intrínseco) são maiores, arredondadas, apresentam núcleo central e intensa acidofilia citoplasmática.', 'HE', 100, 196, '', '', 0),
(41, 2, 5, 'Estômago – glândulas gástricas, muscular da mucosa e parte da submucosa', 'estomago-100', 'Notam-se glândulas gástricas envolvidas por uma lâmina própria (córion) de tecido conjuntivo frouxo que se estende até a muscular da mucosa. É possível identificar também neste aumento células musculares lisas (provenientes da muscular da mucosa) na lâmina própria entre as glândulas gástricas. Nas glândulas gástricas, dois tipos celulares estão bem evidentes: células parietais e células principais. As células principais são menores, apresentando um formato piramidal, núcleos basalmente localizados e acentuada basofilia citoplasmática. Já as células parietais são maiores, arredondadas, apresentam núcleo central e intensa acidofilia citoplasmática. As glândulas gástricas não ultrapassam a muscular da mucosa. Parte da submucosa de tecido conjuntivo denso não modelado foi moderadamente preservada. Destacamos aqui que a lâmina própria da mucosa gástrica exibe fibroblastos e uma matriz extracelular característica do tecido conjuntivo frouxo, porém poucos linfócitos, uma vez que aquele tecido não é muito suscetível a invasão de patógenos provenientes do lúmen estomacal, especialmente devido à presença do muco visível que atapeta a sua superfície interna.', 'HE', 100, 197, '', '', 0),
(42, 2, 5, 'Glândula gástrica – células parietais e principais', 'glandula-gastrica', 'Envolvidas por lâmina própria, as glândulas gástricas aqui exibem seus dois principais tipos celulares claramente. As células principais são menores, apresentando um formato piramidal, núcleos basalmente localizados e acentuada basofilia citoplasmática. Já as células parietais são maiores, arredondadas, apresentam núcleo central e intensa acidofilia citoplasmática. A lâmina própria mostra material extracelular característico do tecido conjuntivo frouxo, núcleos de fibroblastos, diversos capilares sanguíneos e célula muscular lisa proveniente da muscular da mucosa.', 'HE', 400, 198, '', '', 0),
(43, 2, 5, 'Intestino delgado – prega e vilosidades', 'intestino-delgado', 'A superfície interna do intestino delgado mostra projeções importantes em direção ao seu lúmen conhecidas como pregas, cada prega é uma evaginação de submucosa com constituída por tecido conjuntivo denso bem vascularizado e delimitada pela muscular da mucosa, a partir da qual nota-se uma lâmina própria de tecido conjuntivo frouxo rico em linfócitos envolvendo criptas (glândulas) de Lieberkühn e constituindo o centro de diversas vilosidades, evaginações de mucosa com revestimento de epitélio cilíndrico simples que mostra enterócitos (células cilíndricas absortivas) entremeados por células caliciformes produtoras de muco. Cada enterócito exibe especializações da membrana plasmática apical conhecidas como microvilosidades. Em conjunto, pregas, vilosidades e microvilosidades aumentam em mais de 500 vezes a superfície de absorção do intestino delgado. Neste aumento, notam-se o centro de uma prega formado por submucosa, diversas de suas vilosidades e criptas associadas, e algumas vilosidades de outra prega intestinal. A lâmina própria no centro de cada vilosidade e entre as criptas neste aumento é pouco nítida. ', 'HE', 40, 199, '', '', 0);
INSERT INTO `wp_laminas` (`ID`, `ID_materia`, `ID_assunto`, `nome`, `slug`, `descricao`, `coloracao`, `aumento`, `imagem`, `detalhes`, `mascaras`, `b_del`) VALUES
(45, 2, 5, 'Intestino delgado - vilosidades ', 'intestino-delgado-200x', 'Observa-se uma vilosidade em corte longitudinal, outra em corte oblíquo/tangencial e parte do epitélio cilíndrico simples absortivo de uma terceira vilosidade. O campo microscópico não mostra criptas (glândulas) de Lieberkühn que liberam seu produto de secreção no lúmen intestinal entre as criptas. O epitélio cilíndrico simples absortivo e lâmina própria estão aqui muito bem evidentes. O epitélio cilíndrico simples exibe enterócitos (células cilíndricas absortivas) entremeados por células caliciformes produtoras de muco. Cada enterócito exibe especializações da membrana plasmática apical conhecidas como microvilosidades, que forma em conjunto a planura estriada, mais evidente em cortes corados por ácido periódico mais reativo de Schiff (do inglês PAS – Periodic Acid-Schiff reagent), que cora em vermelho o glicocálice associado às proteínas transmembrana das vilosidades. O enterócito mostra núcleo oval em seu pólo basal e com longo eixo paralelo ao longo eixo da célula. Núcleos de linfócitos intimamente associados ao epitélio cilíndrico simples absortivo por estarem envolvidos por microdobras da membrana plasmática basal de células M também podem ser notados. A lâmina própria mostra-se constituída por tecido conjuntivo frouxo rico em linfócitos e sustentando diversos capilares sanguíneos que exibem hemácias. Quilíferos (capilares linfáticos), também presentes na lâmina própria geralmente colabam com o processamento histológico e não são bem evidentes. Células musculares lisas dispersas também estão presentes na lâmina própria do centro da vilosidade e são importantes para a motilidade desta estrutura estimulando a absorção, porém não estão bem evidentes na imagem.', 'HE', 200, 200, '', '', 0),
(46, 2, 5, 'Intestino delgado – submucosa, vilosidades e criptas de Lieberkühn', 'intestino-delgado-criptas-Lieberkuhn', 'Corte de parte de uma prega do intestino delgado. Notam-se vilosidades, criptas (glândulas) de Lieberkühn e parte da submucosa que constitui o centro da prega exibindo uma veia com parede caracteristicamente delgada, vários eritrócitos, poucos leucócitos e uma válvula se projetando para a sua luz. As vilosidades evidentes no campo microscópico em análise são projeções digitiformes da mucosa com: 1. eixo central de tecido conjuntivo frouxo (lâmina própria), com fibroblastos e todos os seus produtos que constituem fibras e substância fundamental extracelulares, além de linfócitos, células musculares lisas dispersas, capilares sanguíneos e linfáticos (estes, também chamados de quilíferos, não são vistos no preparado em análise); 2. revestimento de epitélio simples cilíndrico constituído, que na região observada é formado essencialmente por dois tipos celulares (enterócitos, células cilíndricas absortivas ricas em microvilosidades apicais que formam a planura estriada; e células caliciformes fracamente coradas em seu citoplasma apical devido à presença de grânulos contendo muco, não corados por HE). As criptas de Lieberkühn são observadas envolvidas por lâmina própria e dentre os tipos celulares que as compõem distinguem-se no espécime enterócitos, células caliciformes e células de Paneth, que se localizam na base das criptas e contêm grânulos citoplasmáticos apicais intensamente acidófilos. Estes grânulos apresentam defensinas (proteínas antimicrobianas) e lisozima (enzima que quebra a parede de peptidoglicano das bactérias, contribuindo para o controle da microbiota na luz intestinal). Nota-se que as criptas não ultrapassam a muscular da mucosa (não muito evidente aqui). Uma fenda observada na vilosidade entre o epitélio e a lâmina própria constitui um artefato do processamento histológico.', 'HE', 200, 201, '', '', 0),
(47, 2, 5, 'Intestino delgado – submucosa, cripta de Lieberkühn, células de Paneth', 'intestino-delgado-400', 'Notam-se bases de duas vilosidades, criptas (glândulas) de Lieberkühn (uma em corte longitudinal e outra em corte tangencial no canto inferior direito), e parte da submucosa que constitui o centro da prega exibindo uma veia com parede caracteristicamente delgada, vários eritrócitos e uma válvula se projetando para a sua luz. A muscular da mucosa mostra-se evidente neste aumento. A cripta de Lieberkühn no centro campo microscópico é observada envolvida por lâmina própria e mostra, dentre outros tipos celulares, enterócitos, células caliciformes e células de Paneth, que se localizam em sua base e contêm grânulos citoplasmáticos apicais intensamente acidófilos. Estes grânulos apresentam defensinas (proteínas antimicrobianas) e lisozima (enzima que quebra a parede de peptidoglicano das bactérias, contribuindo para o controle da microbiota na luz intestinal). Nota-se que as criptas não ultrapassam a muscular da mucosa.', 'HE', 400, 202, '', '', 0),
(48, 2, 5, 'Intestino delgado – camadas submucosa, muscular e serosa', 'intestino-delgado-40', 'Notam-se camada submucosa (tecido conjuntivo denso e grandes vasos sanguíneos), parte da mucosa com lâmina própria e algumas criptas, camada muscular circular interna (fibras em corte transversal), camada muscular longitudinal externa (fibras em corte longitudinal) e serosa. Parte do plexo mioentérico de Auerbach é perceptível entre as duas camadas musculares. ', 'HE', 40, 0, '', '', 0),
(49, 2, 5, 'Intestino delgado – plexo de Auerbach, camadas musculares e serosa', 'intestino-delgado-plexo-de-auerbach', 'Notam-se camada muscular circular interna (fibras em corte transversal), camada muscular longitudinal externa (fibras em corte longitudinal) e serosa. Parte do plexo mioentérico de Auerbach é perceptível entre as duas camadas musculares. Um artefato de técnica (fortemente vermelho) é perceptível no campo microscópico. ', 'HE', 100, 203, '[]', '', 0),
(50, 2, 5, 'Intestino grosso – mucosa ', 'intestino-grosso', 'A superfície interna do intestino grosso é lisa, pois este segmento do tubo digestório é desprovido de pregas e vilosidades. Por outro lado, a mucosa do intestino grosso mostra criptas (glândulas) de Lieberkühn bem mais profundas que aquelas do intestino delgado, com expressiva quantidade de células caliciformes, e envolvidas por tecido conjuntivo frouxo (lâmina própria) com muitos linfócitos, que podem formar nódulos em algumas regiões. Alguns vasos sanguíneos mais calibrosos são perceptíveis na lâmina própria. As criptas são observadas aqui em cortes longitudinal, oblíquo e transversal. A mucosa do intestino grosso é revestida por células cilíndricas absortivas em camada única (epitélio simples cilíndrico). Este epitélio absorve íons cloreto e sódio, que é acompanhado por água. Assim, à medida que segue em direção ao canal anal, o bolo fecal vai sendo formado tornando se progressivamente mais pastoso. O muco produzido pelas glândulas facilita o transporte do bolo fecal com o menor atrito possível com o epitélio de revestimento interno do intestino grosso. Ao mesmo tempo que o epitélio aqui descrito é favorável à absorção de íons e água, é mais vulnerável à invasão de patógeno quando comparado a um epitélio estratificado. E esta é a razão da expressiva quantidade de linfócitos na lâmina própria do intestino grosso, estrategicamente posicionados para resposta imunológica em caso de invasão de patógeno. Nota-se que as glândulas não ultrapassam a muscular da mucosa. Neste espécime, as demais camadas do tubo digestório não são observadas. ', 'HE', 40, 204, '', '', 0),
(51, 2, 5, 'Intestino grosso – mucosa', 'intestino-grosso-100', 'Nota-se parte da mucosa do intestino grosso. As criptas (glândulas) de Lieberkühn bem profundas e ricas em células caliciformes estão bem evidentes, envolvidas por tecido conjuntivo frouxo (lâmina própria) com muitos linfócitos. As criptas são observadas aqui em corte longitudinal. A mucosa do intestino grosso é revestida por células cilíndricas absortivas em camada única (epitélio simples cilíndrico). Algumas áreas estão exibindo o referido epitélio em corte tangencial, dando a impressão de epitélio estratificado. Este epitélio absorve íons cloreto e sódio, que é acompanhado por água. Assim, à medida que segue em direção ao canal anal, o bolo fecal vai sendo formado tornando se progressivamente mais pastoso. O muco produzido pelas glândulas facilita o transporte do bolo fecal com o menor atrito possível com o epitélio de revestimento interno do intestino grosso. Ao mesmo tempo que o epitélio aqui descrito é favorável à absorção de íons e água, é mais vulnerável à invasão de patógeno quando comparado a um epitélio estratificado. E esta é a razão da expressiva quantidade de linfócitos na lâmina própria do intestino grosso, estrategicamente posicionados para resposta imunológica em caso de invasão de patógeno. Neste espécime, as demais camadas do tubo digestório não são observadas.', 'HE', 100, 205, '', '', 0),
(52, 2, 5, 'Apêndice – mucosa com glândulas de Lieberkühn e nódulo linfóide, submucosa e parte da camada muscula', 'apendice', 'Corte transversal do apêndice, no qual observam-se mucosa com glândulas de Lieberkühn e nódulo linfóide, submucosa e parte da camada muscular externa. A estrutura geral do apêndice obedece à do intestino grosso. A mucosa apresenta-se revestida por epitélio cilíndrico simples com poucas células caliciformes (não muito evidente neste aumento) e a lâmina própria sustenta o mencionado epitélio e criptas de Lieberkühn menos numerosas. A lâmina própria e a submucosa apresentam um grande número de linfócitos infiltrados, que formam neste órgão diversos nódulos linfóides. Sendo um divertículo do ceco com fundo cego e parede delgada, uma vez diagnosticada apendicite, o paciente deve ser submetido a cirurgia de emergência para remoção do apêndice e sutura adequada, evitando extravasamento de fezes para a cavidade peritoneal, situação de alto risco de grave infecção.  ', 'HE', 40, 206, '', '', 0),
(53, 2, 5, 'Apêndice – parte da mucosa, submucosa, muscular externa e serosa', 'apendice-parte-da-mucosa', 'Notar a presença de parte da mucosa, camadas musculares externas circular interna (células musculares lisas em corte longitudinal) e longitudinal externa (células musculares lisas em corte transversal), além da camada serosa, exibindo tecido conjuntivo frouxo revestido por mesotélio. \r\n', 'HE', 40, 207, '', '', 0),
(54, 2, 6, 'Fígado – trato portal ', 'figado', 'O fígado apresenta uma consistência mole, uma vez que neste órgão predominam placas de células epiteliais glandulares conhecidas como hepatócitos, havendo uma quantidade expressivamente menor de tecido conjuntivo. Este, por sua vez, constitui a cápsula de Glisson (tecido conjunto denso que envolve o fígado e é mais espesso no hilo hepático, onde entram artéria hepática e veia porta e saem os ductos biliares hepáticos direito e esquerdo), o tecido conjuntivo frouxo dos tratos portais e o delgado tecido reticular contido no espaço de Disse, localizado entre a placa de hepatócitos e as células endoteliais do sinusóide à ela adjacente. paralelo. No corte em tela, notam-se um trato portal e placas de hepáticos de lóbulos hepáticos adjacentes. Os limites entre um lóbulo e outro pouco nítidos devido à escassez de tecido conjuntivo entre eles, exceto nos tratos portais. É possível notar já neste pequeno aumento o tecido conjuntivo frouxo do trato portal e seus principais componentes: vênula porta terminal, arteríola ramo da artéria hepática e ducto bilar. Os pequenos espaços em branco perceptíveis entre as placas de hepatócitos são os sinusóides hepáticos que conduzem sangue proveniente da veia porta em 75% e da artéria hepática em 25% aproximadamente.\r\n', 'HE', 40, 208, '', '', 0),
(55, 2, 6, 'Fígado – trato portal (HE - 100x)', 'figado-100', 'O corte em análise exibe placas de hepatócitos de lóbulos hepáticos adjacentes e um trato portal. Este, é caracterizado por conter três estruturas principais (tríade porta) sustentadas por um tecido conjuntivo frouxo. A maior estrutura é a vênula porta terminal (ramo da veia porta), com uma parede delgada, revestida por células endoteliais. A vênula porta terminal ramifica-se em vênulas distribuidoras, e estas, por sua vez, ramificam-se em vênulas de entrada que fornecem sangue venoso para os sinusóides. Vasos de parede espessa e diâmetro menor são arteríolas decorrentes da artéria hepática, sendo a de maior diâmetro aqui percebida a arteríola ramo da artéria hepática e a de menor diâmetro possivelmente uma arteríola distribuidora. A arteríola ramo da artéria hepática ramifica-se em arteríolas distribuidoras, e estas, por sua vez, ramificam-se em arteríolas de entrada que fornecem sangue arterial para os sinusóides. O ducto biliar diferencia-se das arteríolas por apresentarem revestimento epitelial simples cúbico e não mostrarem músculo liso em sua parede. Vasos linfáticos também estão presentes no trato portal, porém, como suas paredes delicadas frequentemente colapsam durante o processamento histológico, são dificilmente identificados. Os sinusóides entre as placas de hepatócitos também são perceptíveis e suas células de revestimento se distinguem pelos núcleos achatados e citoplasma pouco evidente. \r\n', 'HE', 100, 209, '', '', 0),
(56, 2, 6, 'Fígado – lóbulo hepático', 'figado-lobulo-hepatico', 'Nota-se parte de um lóbulo hepático exibindo em seu centro o ramo inicial da veia hepática, conhecida como vênula centrolobular. Para esta, placas de hepatócitos com ramificações e anastomoses irregulares convergem lembrando a estrutura de uma esponja. Os hepatócitos são células poliédricas grandes, de núcleo esférico, cromatina dispersa e nucléolo evidente. A acidofilia e o aspecto esponjoso do citoplasma do hepatócito decorrem do grande número de mitocôndrias (até 2.000 por hepatócito) e de gotículas lipídicas (pedidas na diafanização da amostra) e grânulos de glicogênio (não corados por HE), características associadas ao intenso metabolismo desta célula, incluindo o processamento e armazenamento temporário de lipídeos e carboidratos. Entre as placas de hepatócitos estão localizados os sinusóides, cujas células de revestimento se distinguem pelos núcleos achatados e citoplasma pouco evidente.\r\n', 'HE', 100, 210, '', '', 0),
(57, 2, 6, 'Fígado – hepatócitos e sinusóides', 'figado-hepatocitos-e-sinusoides', 'Observam-se sinusóides e placas de hepatócitos. Estas são células poliédricas grandes, de núcleo esférico, cromatina dispersa e nucléolo evidente. A acidofilia e o aspecto esponjoso do citoplasma do hepatócito decorrem do grande número de mitocôndrias (até 2.000 por hepatócito) e de gotículas lipídicas (pedidas na diafanização da amostra) e grânulos de glicogênio (não corados por HE), características associadas ao intenso metabolismo desta célula, incluindo o processamento e armazenamento temporário de lipídeos e carboidratos. Os sinusóides são os leitos vasculares paralelos às placas de hepatócitos, exibindo células de revestimento de núcleo achatado e citoplasma pouco evidente.', 'HE', 200, 211, '', '', 0),
(58, 2, 6, 'Fígado – hepatócitos e sinusóides', 'figado-hepatocitos-e-sinusoides-400', 'Sinusóides e placas de hepatócitos mostram-se aqui ainda mais evidentes. Os hepatócitos poliédricos, grandes, de núcleo esférico com cromatina dispersa e nucléolo evidente, e citoplasma caracteristicamente acidófilo e esponjoso. Os sinusóides são os leitos vasculares paralelos às placas de hepatócitos, exibindo células de revestimento de núcleo achatado e citoplasma pouco evidente. O espaço de Disse, localizado entre a célula endotelial do sinusóide e o hepatócito, não é perceptível em microscopia de luz, mesmo nos maiores aumentos por ela providos.', 'HE', 400, 212, '', '', 0),
(59, 2, 6, 'Pâncreas exócrino – lóbulos pancreáticos ', 'pancreas-exocrino-lobulo-pancreaticos', 'Observam-se os lóbulos pancreáticos separados por septos de tecido conjuntivo provenientes da delgada cápsula do pâncreas. Estes septos podem exibir quantidade importante de células adiposas. No interior desses lóbulos, observam-se ácinos e ductos (pouco evidentes neste aumento), responsáveis, respectivamente, por produzir e conduzir a secreção exócrina. Cada ácino é esférico e formado por células acinosas piramidais com polos bem diferenciados: a porção basal é basófila (periferia do ácino), enquanto o polo apical é intensamente acidófilo (centro do ácino). O tecido endócrino do pâncreas é constituído pelas ilhotas de pancreáticas, aqui não visualizadas. Registre-se que tecido conjuntivo frouxo envolve ácinos e ductos dentro dos lóbulos, sustentando também capilares sanguíneos e terminações nervosas parassimpáticas pós-ganglionares, elementos fundamentais para o funcionamento adequado do pâncreas. \r\n', 'HE', 40, 213, '', '', 0),
(60, 2, 6, 'Pâncreas exócrino – ducto e ácinos', 'pancreas-exocrino-ducto-e-acinos', 'Nota-se parte de um lóbulo pancreático, com seus ácinos esféricos e um ducto intralobular em corte longitudinal, exibindo revestimento interno de epitélio simples cúbico. Cada ácino é esférico e formado por células acinosas piramidais com polos bem diferenciados: a porção basal é basófila (periferia do ácino), enquanto o polo apical é intensamente acidófilo (centro do ácino). Registre-se que tecido conjuntivo frouxo envolve ácinos e ductos dentro do lóbulo, sustentando também capilares sanguíneos e terminações nervosas parassimpáticas pós-ganglionares, elementos fundamentais para o funcionamento adequado do pâncreas. Núcleos de células centroacinares são também perceptíveis neste aumento. Estas células constituem o revestimento interno dos primeiros ductos a receberem a secreção exócrina produzida pelas células acinosas, os ductos intercalares.   \r\n', 'HE', 400, 214, '', '', 0),
(61, 2, 6, 'Pâncreas exócrino - ducto intralobular, ácinos serosos e células centroacinares ', 'pancreas-exocrino-ducto-intralobular', 'Nota-se parte de um lóbulo pancreático, com seus ácinos esféricos e um ducto intralobular em corte transversal, exibindo revestimento interno de epitélio simples cúbico. Cada ácino é esférico e formado por células acinosas piramidais com polos bem diferenciados: a porção basal é basófila (periferia do ácino), enquanto o polo apical é intensamente acidófilo (centro do ácino). Registre-se que tecido conjuntivo frouxo envolve ácinos e ductos dentro do lóbulo, sustentando também capilares sanguíneos e terminações nervosas parassimpáticas pós-ganglionares, elementos fundamentais para o funcionamento adequado do pâncreas. Núcleos de células centroacinares são também perceptíveis neste aumento. Estas células constituem o revestimento interno dos primeiros ductos a receberem a secreção exócrina produzida pelas células acinosas, os ductos intercalares. Percebe-se que, coerentemente, o conteúdo intraluminal ducto intralobular em corte transversal é compatível com o conteúdo do polo apical das células acinosas. ', 'HE', 400, 215, '', '', 0),
(62, 2, 6, 'Vesícula biliar – camadas mucosa e muscular', 'vesicula-biliar-mucosa-e-muscular', 'Observa-se parte da parede de uma vesícula biliar mostrando a mucosa pregueada. O epitélio de revestimento interno deste órgão é do tipo simples cilíndrico e repousa sobre uma lâmina própria constituída por tecido conjuntivo frouxo, rico em fibras elásticas e vasos sanguíneos que drenam a água absorvida da bile durante seu processo de concentração. As fibras da camada muscular dispõem-se em orientações longitudinal, transversal e oblíqua, mas não chegam a constituir camadas distintas. Externamente, há a presença de uma adventícia ou serosa (aqui não visualizadas).\r\n', 'HE', 40, 216, '', '', 0),
(63, 2, 6, 'Vesícula biliar – camadas mucosa e muscular', 'vesicula-biliar-mucosa-e-muscular-100', 'Observa-se parte da parede de uma vesícula biliar mostrando a mucosa pregueada. O epitélio simples de revestimento é constituído por células cilíndricas altas com os núcleos localizados basalmente. Notam-se também numerosas microvilosidades no polo apical dessas células. A lâmina própria é constituída por tecido conjuntivo frouxo, rico em fibras elásticas e muitos vasos sanguíneos que drenam a água absorvida da bile durante seu processo de concentração. As fibras da camada muscular estão evidentes canto inferior direito da imagem', 'HE', 100, 217, '', '', 0),
(64, 2, 6, 'Vesícula biliar – epitélio simples cilíndrico', 'vesicula-biliar-epitelio-simples', '\r\nApenas a mucosa da vesícula biliar (lâmina própria e epitélio simples cilíndrico) é aqui observada em grande aumento. Destacam-se os núcleos em localização basal e as numerosas microvilosidades no polo apical das células absortivas.', 'HE', 200, 218, '', '', 0),
(65, 2, 7, '3.1 Hipófise – Pars Distalis – células basófilas e acidófilas', 'hipofise', 'Notam-se características estruturais de afinidade tintorial compatíveis com a pars distalis da hipófise. Observam-se cordões de células produtoras de hormônios exibindo afinidade citoplasmática para hematoxilina (cromófilas basófilas) ou eosina (cromófilas acidófilas). Células epiteliais glandulares endócrinas com citoplasma fracamente corado (cromófobas) também podem ser observadas. Os mencionados cordões mostram-se sustentados por um tecido conjuntivo frouxo rico em capilares sanguíneos fenestrados com diafragma, aqui evidentes devido às hemácias em seus leitos. Fenestras e diafragma dos capilares aqui referidos são vistos apenas em microscopia de eletrônica. O tecido conjuntivo ora apresentado é rico em fibras reticulares, que carecem de outros tipos de coloração para identificação. As células basófilas produzem os hormônios tireotrófico (TSH), gonadotróficos (hormônio luteinizante – LH e hormônio folículo estimulante – FSH) e adrenocorticotrófico (ACTH). As células acidófilas produzem prolactina (PRL) e hormônio do crescimento (GH).', 'HE', 400, 219, '', '', 0),
(66, 2, 7, '3.2 Hipófise – Pars Nervosa – corpos de Herring', 'hipofise-pars-nervosa', 'Notam-se características estruturais de afinidade tintorial compatíveis com a pars nervosa da hipófise. Observam-se axônios não mielinizados cujos corpos celulares encontram-se nos núcleos supra-óptico e paraventricular do hipotálamo. Dentro desses axônios, hormônios produzidos pelo hipotálamo (oxitocina e hormônio antidiurético – ADH, também denominado vasopressina) são transportados por neurofisinas até as suas extremidades, de onde são liberados e coletados por capilares fenestrados com diafragma. A extremidade do axônio dilatada pelo acúmulo de hormônios provenientes do hipotálamo é aqui denominada corpo de Herring ou corpo neurossecretor. Notam-se ainda núcleos de pituícitos, células de natureza glial com função de sustentação.', 'HE', 400, 220, '', '', 0),
(67, 2, 7, '3.3 Hipófise – Pars Nervosa – pigmentos de lipocromo', 'hipofise-pars-nervosa-pigmentos', 'Notam-se características estruturais de afinidade tintorial compatíveis com a pars nervosa da hipófise. Destacam-se aqui grânulos pardacentos de lipofuscina, um pigmento de desgaste visto geralmente em células de vida longa que não se dividem, como neurônios e células musculares cardíacas. ', 'HE', 400, 221, '', '', 0),
(68, 2, 7, '3.4 Hipófise – Pars Intermedia – cistos de Rathke', 'hipofise-pars-intermedia', 'O campo microscópico inclui parte da pars distalis com predominância de células cromófilas acidófilas, uma área da pars nervosa e uma porção da pars intermedia exibindo espaços císticos preenchidos por material eosinófilo e denominado cistos de Rathke.', 'HE', 200, 222, '', '', 0),
(69, 2, 7, '3.5 Tireoide – folículos tireoideanos', 'tireoide', 'Observam-se inúmeras vesículas (também denominadas folículos) delimitadas por uma única camada de células epiteliais secretoras. Normalmente, o núcleo esférico dessas células denuncia o aspecto cúbico, mas a sua altura pode variar de acordo com a atividade funcional do folículo é notável em maior aumento. Os hormônios secretados por estas células (T3 e T4) estão armazenados sob a forma inativa (tireoglobulina) no interior do folículo, formando o coloide tireoidiano, intensamente acidófilo.', 'HE', 40, 223, '', '', 0),
(70, 2, 7, '3.6 Tireoide – células foliculares e coloide', 'tireoide-celular-foliculares', 'Observam-se partes de três folículos tireoidianos delimitados por uma única camada de células epiteliais secretoras. Normalmente, o núcleo esférico dessas células denuncia o aspecto cúbico, como aqui notado, mas a sua altura pode variar de acordo com a atividade funcional. Os hormônios secretados por estas células (T3 e T4) estão armazenados sob a forma inativa (tireoglobulina) no interior do folículo, formando o coloide tireoidiano, intensamente acidófilo. Sustentando os folículos, um tecido conjuntivo frouxo rico em capilares fenestrados com diafragma é visualizado. Nele, núcleos achatados fortemente corados são de fibroblastos ou células endoteliais dos capilares. As células C (ou parafoliculares), produtoras de calcitonina, não são visualizadas no campo microscópico.', 'HE', 400, 224, '', '', 0),
(71, 2, 7, '3.7 Tireoide – folículos e estroma com capilares sanguíneos ', 'tireoide-foliculos', 'Observam-se folículos tireoidianos delimitados por uma única camada de células epiteliais secretoras cúbicas ou pavimentosas (estas, inativas). Sustentando os folículos, um tecido conjuntivo frouxo rico em capilares fenestrados com diafragma é visualizado. Nele, núcleos achatados fortemente corados são de fibroblastos ou células endoteliais dos capilares. As células C (ou parafoliculares), produtoras de calcitonina, não são bem visualizadas no campo microscópico, apresentam citoplasma mais claro e não entram em contato com o coloide.', 'HE', 400, 225, '', '', 0),
(72, 2, 7, '3.8 Córtex suprarrenal incluindo cápsula e septos', 'cortex-suprarrenal', 'Nota-se uma espessa cápsula de tecido conjuntivo denso modelado emitindo septos em direção ao parênquima glandular. A cápsula aqui identificada associa-se a grande quantidade de tecido adiposo unilocular (não preservado no processamento histológico). O parênquima do córtex suprarrenal mostra-se fracamente corado, uma vez que é constituído de células produtoras de hormônios esteroides a partir do colesterol, que neste caso foi perdido durante da diafanização da amostra com xilol. Os pontos basófilos observados neste aumento são os núcleos das células do parênquima cortical. Este exibe três zonas concêntricas da cápsula para dentro: glomerulosa (logo abaixo da cápsula e sintetizadora de mineralocorticoides, sob o estímulo de angiotensina II e hormônio adrenocorticotrófico – ACTH, produzido por células basófilas da pars distalis da adenohipófise), fasciculada e reticulada (não distinguíveis neste aumento e produtoras, respectivamente, de glicocorticoides e andrógenos sob o estímulo de ACTH).', 'HE', 40, 226, '[]', '', 0),
(73, 2, 7, '3.9 Córtex suprarrenal – zona fasciculada', 'cortex-suprarrenal-zona-fasciculada', 'Observa-se a camada concêntrica intermediária do córtex suprarrenal, denominada zona fasciculada, que é a maior camada do córtex, correspondente a 80% do volume total da glândula. Células parenquimatosas poliédricas de núcleo central, maiores que as células das zonas glomerulosa e reticular, estão dispostas em colunas radiais de uma ou duas células de espessura. Estas células são denominadas espongiócitos, pois exibem o citoplasma vacuolizado em decorrência da perda de suas gotículas lipídicas no processamento histológico de rotina. Produtores dos glicocorticoides cortisol e corticosterona sob o estímulo do ACTH, os espongiócitos exibem ao microscópio eletrônico riqueza de mitocôndrias esféricas com cristas tubulares (estas organelas conferem a acidofilia citoplasmática ao microscópio de luz), retículo endoplasmático liso e gotículas lipídicas. Estas células produzem glicocorticoides a partir do colesterol, por meio de enzimas mitocondriais e do retículo endoplasmático liso. Ademais, os espongiócitos mostram expressiva quantidade de grânulos de lipofuscina, que aparecem pardacentos na microscopia de luz. O delgado tecido conjuntivo frouxo que sustenta os cordões de espongiócitos também exibe capilares sanguíneos fenestrados com diafragma supridos pelas artérias corticais curtas. Estes capilares são importantes para receberem os hormônios ali produzidos, sendo identificados neste aumento em especial pela presença de hemácias em seu interior. Registre-se que as fenestras com diafragma dos referidos capilares são estruturas notáveis em microscopia eletrônica.', 'HE', 200, 227, '', '', 0),
(74, 2, 7, '3.10 Pâncreas endócrino', 'pancreas-endocrino', 'O pâncreas endócrino é constituído por grupos esféricos de células endócrinas denominados ilhotas de Langerhans, que se encontram espalhadas em meio aos ácinos pancreáticos. O centro do campo microscópico exibe aqui uma ilhota de Langerhans. Nela observam-se cordões de células endócrinas pequenas e fracamente coradas no citoplasma, exibindo núcleo central. Estas células são sustentadas por um delgado tecido conjuntivo reticular que sustenta também capilares sanguíneos fenestrados com diafragma, perceptíveis por suas hemácias. Os cinco tipos de células parenquimatosas do pâncreas endócrino (beta, alfa, delta, PP e G) não são distinguíveis em cortes corados por HE, mas adequadamente marcados imuno-histoquímica e diferenciados em microscopia eletrônica especialmente pelo tamanho e pela elétron-densidade de seus grânulos. Estas células exibem características ultraestruturais (vistas ao microscópio eletrônico) de células produtoras de proteínas. Uma cápsula delicada dificilmente identificada circunda a ilhota.', 'HE', 400, 228, '', '', 0),
(75, 2, 8, '4.1 Testículo ', 'testiculo', 'Observam-se três lóbulos testiculares, que são incompletamente separados por septos de tecido conjuntivo denso provenientes da túnica albugínea, cápsula de tecido conjuntivo denso aqui não visualizada e que fica envolvida pela túnica vaginal e pele do escroto. No interior de cada lóbulo, notam-se túbulos seminíferos altamente enovelados, em cortes oblíquos e transversais. Estes são revestidos internamente pelo epitélio seminífero (também denominado germinativo), constituído por quatro a oito camadas de células em diferentes estágios de maturação, envolvidas nos processos de espermatocitogênese, meiose e espermiogênese, etapas da produção de espermatozoides. Neste aumento, as referidas células não são identificadas. Envolvendo os túbulos seminíferos, observa-se um tecido conjuntivo frouxo bem vascularizado denominado túnica própria, que além dos seus elementos característicos, sustentam grupos de células de Leydig produtoras de testosterona e também não perceptíveis neste aumento.', 'HE', 40, 229, '', '', 0),
(76, 2, 8, '4.2 Túbulo seminífero', 'tubulo_seminifero', 'O preparado em análise não exibe a túnica própria preservada, mas o epitélio seminífero (germinativo) de um túbulo seminífero e parte do mencionado epitélio de outro túbulo seminífero do testículo. Distinguem-se os seguintes tipos celulares que compõem o epitélio de revestimento interno dos túbulos seminíferos, a partir da lâmina basal: a espermatogônia é identificada por localizar-se próxima à lâmina basal e apresentar núcleo grande e de cromatina condensada; o espermatócito primário caracteriza-se pela presença de núcleo grande e cromatina de aspecto granular; o espermatócito secundário não é perceptível; as espermátides, mais próximas ao lúmen, apresentam núcleo menor e bem corado, fruto de divisões meióticas, de aspecto esférico ou progressivamente achatado; finalmente, os núcleos dos espermatozoides assumem forma pequena e pontiaguda. As células de Sertoli são normalmente observadas próximas à lâmina basal, possuindo núcleo triangular ou oval com nucléolo evidente. A presença de células mióides (miofibroblastos) envolvendo os túbulos denuncia que o espécime não é de amostra humana.', 'HE', 400, 230, '', '', 0),
(77, 2, 8, '4.3 Células de Leydig', 'celulas_de_leyding', 'Notam-se partes de quatro túbulos seminíferos e ao centro do campo microscópico a túnica própria bem vascularizada sustentando células de Leydig. Estas são responsáveis pela síntese e secreção de testosterona em resposta ao hormônio luteinizante – LH produzido por células basófilas da pars distalis da hipófise, apresentam núcleo redondo com cromatina dispersa e nucléolo evidente, enquanto o citoplasma se apresenta extenso, eosinofílico e esponjoso. A acidofilia decorre da grande quantidade de mitocôndrias com cristas tubulares e o aspecto esponjoso decorre da remoção de gotículas lipídicas pelo xilol na etapa de diafanização do processamento histológico. ', 'HE', 400, 231, '', '', 0),
(78, 2, 8, '4.4 Epidídimo', 'eppididimo', 'Nota-se parte do epidídimo, um túbulo longo e extremamente contorcido, em cortes oblíquos e transversais. Seu revestimento interno é de epitélio pseudoestratificado cilíndrico com estereocílios (microvilosidades longas que se ramificam e se anastomosam irregularmente). Abaixo do epitélio, localiza-se tecido conjuntivo frouxo rico em células musculares lisas que se organizam em uma camada circular. O campo microscópico também exibe túnica albugínea e septo de tecido conjuntivo denso por ela emitido.', 'HE', 100, 232, '', '', 0),
(79, 2, 8, '4.5 Epidídimo', 'epididimo_400x', 'Nota-se parte do epidídimo em maior aumento e em corte transversal. Seu revestimento interno é de epitélio pseudoestratificado cilíndrico com estereocílios (microvilosidades longas que se ramificam e se anastomosam irregularmente). Neste epitélio, núcleos de dois tipos celulares são identificados: aqueles arredondados e próximos à lâmina basal são de células basais e os mais próximos ao lúmen e ovais são das células principais com estereocílios. Abaixo do epitélio, localiza-se tecido conjuntivo frouxo rico em células musculares lisas que se organizam em uma camada circular.\r\n', 'HE', 400, 233, '', '', 0),
(80, 2, 8, '4.6 Canal deferente', 'canal_deferente', ' \r\nNota-se em corte transversal o canal deferente, tubo muscular de parede espessa com função de conduzir os espermatozoides do epidídimo para a uretra. Internamente, encontra-se revestido por epitélio pseudoestratificado cilíndrico com estereocílios, semelhante ao do epidídimo, porém mais baixo. A lâmina própria é escassa e abaixo desta encontra-se uma espessa parede muscular lisa dividida em três camadas de difícil distinção. Externamente, observa-se a camada adventícia de tecido conjuntivo frouxo (aqui não perceptível). ', 'HE', 100, 234, '', '', 0),
(81, 2, 8, '4.7 Canal deferente ', 'canal_deferente_2', 'cilíndrico com estereocílios, semelhante ao do epidídimo, porém mais baixo, escassa lâmina própria e parede muscular lisa.', 'HE', 400, 235, '', '', 0),
(82, 2, 9, '5.1 Ovário', 'ovario', 'Observam-se duas regiões do ovário neste preparado: córtex e medula. O órgão é revestido externamente por epitélio germinativo, formado por epitélio cúbico simples ou pavimentoso, a depender da região, porém pouco perceptível neste aumento. Logo abaixo deste, observa-se a túnica albugínea, constituída de tecido conjuntivo denso, e a porção do córtex que apresenta um estroma com muitos fibroblastos fusiformes (células intersticiais), tendo em sua porção periférica inúmeros folículos em vários estágios de desenvolvimento, distinguíveis em maior aumento. A medula é evidenciada por seu estroma mais frouxo, menos eosinófilo, sendo característica desta área a presença de vasos sanguíneos amplos. ', 'HE', 40, 236, '', '', 0),
(83, 2, 9, '5.2 Folículo ovariano primordial', 'foliculo_ovariano_primordial', 'Nota-se parte do córtex ovariano, com estroma rico em grandes células intersticiais fusiformes, e quatro folículos primordiais (cada um com camada única de células foliculares pavimentosas).\r\n', 'HE', 400, 237, '', '', 0),
(84, 2, 9, '5.3 Folículo ovariano primário unilaminar', 'foliculo_ovariano_primario_unilaminar', 'Observa-se parte do córtex ovariano, com estroma rico em grandes células intersticiais fusiformes, e ao centro um folículo primário unilaminar (com camada única de células foliculares cúbicas).', 'HE', 400, 238, '', '', 0),
(85, 2, 9, '5.4 Folículo ovariano primário multilaminar ', 'foliculo_ovariano_primario_multilaminar', 'Nota-se parte do córtex ovariano, com estroma rico em grandes células intersticiais fusiformes, e ao centro um folículo primário multilaminar (com mais de uma camada de células foliculares cúbicas). Registre-se a presença da zona pelúcida, área amorfa e acidófila, situada entre o ovócito e a camada de células foliculares.\r\n', 'HE', 400, 239, '', '', 0),
(86, 2, 9, '5.5 Folículo ovariano antral', 'foliculo_ovariano_antral', 'Observa-se parte do córtex ovariano, com estroma rico em grandes células intersticiais fusiformes, e ao centro um folículo secundário, também denominado antral (com espaços que acumulam fluido folicular e que coalescem para formar o antro). Nota-se a presença da zona pelúcida, área amorfa e acidófila, situada entre o ovócito e a camada de células foliculares. O folículo maduro, também denomidado de folículo de Graaf exibe corona radiata e cumulos oophorus, porém não é aqui visualizado. Nota-se a presença da zona pelúcida, área amorfa e acidófila, situada entre o ovócito e a camada de células foliculares a partir de folículo primário multilaminar. Parte de um folículo em atresia é identificado no canto inferior direito e células foliculares de um folículo cortado tangencialmente são observadas no centro do canto esquerdo.\r\n', 'HE', 200, 240, '', '', 0),
(87, 2, 9, '5.6 Tuba uterina', 'tuba_uterina', 'Observa-se corte oblíquo de tuba uterina exibindo um lúmen de contorno irregular, uma vez que é revestida internamente por uma mucosa bem pregueada que apresenta um epitélio cilíndrico simples pouco nítido neste aumento, apoiado numa lâmina própria de tecido conjuntivo frouxo bem vascularizado. Notam-se a camada muscular, formada duas faixas de músculo liso (circular interna e longitudinal externa) não distinguíveis aqui, e a túnica serosa com tecido conjuntivo frouxo bem vascularizado e revestimento de mesotélio.', 'HE', 40, 241, '', '', 0),
(88, 2, 9, '5.7 Tuba uterina', 'tuba_uterina_2', 'Nota-se a mucosa da tuba uterina exibindo um epitélio cilíndrico simples constituído por dois tipos celulares: células ciliadas e células secretoras não-ciliadas (pouco nítidas aqui). Esse epitélio está apoiado numa lâmina própria de tecido conjuntivo frouxo bem vascularizado.', 'HE', 400, 242, '', '', 0),
(89, 3, 11, '1.1 Epitélio cilíndrico simples com glândulas exócrinas tubulosas', 'epitelio_cilindrico_simples_com_glandulas_exocrinas_tubulosas_1', 'Nesta lâmina é possível visualizar a mucosa do reto. O epitélio de revestimento deste órgão é classificado como colunar simples, podendo também ser chamado de cilíndrico ou prismático simples. Este epitélio de revestimento apresenta uma importante função de absorção, portanto estas células colunares também são conhecidas como absortivas. Abaixo desse epitélio é possível perceber a presença da lâmina própria ou córion, o qual é constituído por tecido conjuntivo frouxo. Na lâmina própria são observadas inúmeras glândulas exócrinas tubulares simples. Cada glândula tem uma única luz tubular, dentro da qual os produtos de secreção são liberados (observar que neste espécime a luz das glândulas pode estar obliterada). Tais glândulas aparecem em cortes longitudinais (semelhantes a um dedo de luva), cortes transversais (aspecto circular) ou oblíquo (aspecto alongado). Abaixo do tecido conjuntivo nota-se a presença de células musculares lisas, as quais compõem a muscular da mucosa.', 'HE', 100, 243, '', '', 0),
(90, 3, 11, '1.2 Epitélio cilíndrico simples com glândulas exócrinas tubulosas', 'epitelio_cilindrico_simples_com_glandulas_exocrinas_tubulosas_2', 'O epitélio cilíndrico simples apresenta uma única camada de células, que possuem forma e núcleo alongados. Neste tecido epitelial o núcleo celular está presente no pólo basal que é a porção nutritiva, portanto suas células são polarizadas. Cada glândula da mucosa do reto é um tubo circundado de tecido conjuntivo, que desemboca no epitélio de revestimento. Dentre as células epiteliais que constituem esta glândula, destacam-se as células caliciformes, cuja porção apical do citoplasma não é corada.\r\n', 'HE', 400, 244, '', '', 0),
(91, 3, 11, '1.3 Células caliciformes em glândula tubulosa', 'celulas_caliciformes', 'Nesta fotomicrografia destacam-se as células caliciformes da glândula tubulosa simples do reto. A nomenclatura desta célula se deve ao formato de cálice, sendo seu pólo basal estreito, enquanto que o pólo apical é expandido, porção também conhecida como teca. No citoplasma apical acumula-se mucinogênio, uma glicoproteína que não tem boa afinidade aos corantes da técnica de Hematoxilina e Eosina, portanto a porção apical não é corada. Ao ser secretado, o mucinogênio é hidratado, formando a mucina, um gel viscoso e lubrificante que protege a superfície de revestimento do órgão. Por último, notamos que o núcleo celular está presente no pólo basal da célula caliciforme.\r\n', 'HE', 400, 245, '', '', 0),
(92, 3, 11, '1.4 Epitélio pavimentoso simples', 'epitelio_pavimentoso_simples', 'Nesta imagem observamos um vaso sanguíneo cuja parede é revestida por endotélio (epitélio pavimentoso simples). O lúmen do vaso está preenchido por hemácias, assim como pode ser percebido a presença de plasma fixado. Nos arredores da estrutura vascular é possível perceber a presença de tecido conjuntivo frouxo. ', 'HE', 200, 246, '', '', 0),
(93, 3, 11, '1.5 Epitélio pavimentoso simples ', 'epitelio_pavimentoso_simples_2', 'Fotomicrografia de vaso sanguíneo com o epitélio simples pavimentoso em destaque. Nota-se que o endotélio é formado por uma única camada de células achatadas cujos núcleos acompanham este formato.  Esta morfologia celular facilita as trocas entre o vaso sanguíneo e o tecido adjacente. Ainda é possível perceber hemácias no lúmen da estrutura vascular. \r\n', 'HE', 400, 247, '', '', 0),
(94, 3, 11, '1.6 Epitélio estratificado pavimentoso', 'epitelio_estratificado_pavimentoso', 'Nesta lâmina de canal anal está em destaque o epitélio estratificado pavimentoso não queratinizado. Este tecido epitelial é espesso, formado por várias camadas de células as quais podem ser classificadas em: basal, intermediária e superficial. O estrato basal ou germinativo, é a camada mais profunda e apresenta células justapostas com formato cúbico a cilíndrico. Em seguida, observa-se células poligonais ou poliédricas formando o estrato intermediário. À medida que estas células se tornam cada vez mais achatadas, nota-se o estrato superficial.', 'HE', 100, 248, '', '', 0),
(95, 3, 11, '1.7 Epitélio estratificado pavimentoso queratinizado', 'epitelio_estratificado_pavimentoso_queratinizado', 'Lâmina de pele espessa na qual se observa o epitélio estratificado pavimentoso queratinizado da epiderme. Percebemos neste tecido epitelial a presença de 4 camadas celulares: basal, escamosa, granulosa e córnea. Neste aumento está em destaque o estrato córneo de pele espessa, formado por várias camadas de células epiteliais mortas que apresentam o citoplasma preenchido por queratina. A camada córnea aparece acidófila e filamentosa, cuja função é oferecer proteção e evitar a perda de líquido. Nota-se que o epitélio apresenta invaginações para o tecido conjuntivo, denominadas cristas epiteliais (epidérmicas), enquanto que o tecido conjuntivo forma projeções no epitélio, chamadas de papilas conjuntivas (dérmicas). Abaixo do tecido epitelial está presente o tecido conjuntivo (derme).', 'HE', 40, 249, '', '', 0),
(96, 3, 11, '1.8 Epitélio estratificado pavimentoso queratinizado', 'epitelio_estratificado_pavimentoso_queratinizado_2', 'Nesta fotomicrografia está em destaque os estratos basais, escamoso e granuloso do epitélio estratificado pavimentoso queratinizado. É possível verificar uma pequena porção visível da camada córnea. A camada basal é formada por células com morfologia cúbicas a cilíndricas, bem justapostas e com função germinativa. Em seguida, nota-se o estrato escamoso formado por células poligonais a poliédricas e de núcleo central. Acima, percebe-se a presença da camada granulosa, formada por células de núcleo achatado e com a presença de inúmeros grânulo basófilos de querato-hialina.', 'HE', 200, 0, '', '', 0),
(97, 3, 11, '1.9 Glândula exócrina tubulosa composta - ductos e unidades secretoras mistas', 'glandula_exocrina', 'Fotomicrografia de glândula cujas unidades secretoras se organizam em estruturas tubulosa e um sistema ramificado de ductos, portanto sendo classificada como glândula tubulosa composta. As unidades secretoras são formadas por células mucosas envolvidas por semiluas serosas. Os ductos excretores apresentam luz ampla e são constituídos por células epiteliais cúbicas ou cilíndricas. Quanto maior o diâmetro do ducto, mais altas são suas células e podem apresentar mais de uma camada de células. Observar a presença de septos de tecido conjuntivo propriamente dito com vasos sanguíneos e adipócitos uniloculares. ', 'HE', 200, 250, '', '', 0),
(98, 3, 11, '1.10 Glândula exócrina tubulosa composta - unidades secretoras mistas, ou seja, túbulos mucosos com ', 'glandula_exocrina_tubulosa_composta', 'Nesta fotomicrografia de glândula tubulosa composta destaca-se as unidades secretoras mucosas envolvidas por semiluas serosas. A células mucosas apresentam formato piramidal e núcleo achatado no pólo basal. Estas células acumulam em seu citoplasma apical glicoproteínas que apresentam baixa afinidade aos corantes da técnica de Hematoxilina Eosina, por isso se observa este compartimento celular claro (sem coloração). As semiluas serosas apresentam células com morfologia piramidal e núcleo arredondado no pólo basal. O citoplasma apical destas células está preenchido por proteínas, que neste caso tem afinidade pelo corante hematoxilina. \r\n', 'HE', 400, 251, '', '', 0),
(99, 3, 11, '2.1 Fibras reticulares', 'fibras_reticulares', 'Lâmina de fígado corada por sais de prata (impregnação argêntica) é possível evidenciar as fibras reticulares em negro. Estas fibras não são visualizadas em preparados com Hematoxilina-eosina (HE). Observar que elas formam uma rede delicada e emaranhada que sustenta o parênquima hepático e é mais densa na área próxima ao vaso sanguíneo. Essas fibras podem ser vistas em corte longitudinal ou transversal. As outras estruturas do órgão não podem ser bem diferenciadas nesta coloração.\r\n', 'prata', 100, 252, '', '', 0),
(100, 3, 10, '2.2 Fibras elásticas', 'fibras_elasticas', 'Nesta fotomicrografia visualizamos as fibras elásticas em coloração de órceina. Podemos perceber tais fibras em coloração púrpura, formando estruturas delgadas e ramificadas, tanto em cortes longitudinais, quanto em cortes transversais. Além disso, nesta lâmina de pele, encontram-se fibras colágenas coradas mais fracamente e mais espessas do que as elásticas.\r\n', 'orceina', 100, 253, '', '', 0),
(101, 3, 10, '2.3 Tecido conjuntivo frouxo e denso não modelado', 'tecido_conjuntivo_frouxo_e_denso', 'Lâmina de pele, em destaque a epiderme e a derme. A epiderme forma projeções para o tecido conjuntivo denominadas cristas epidérmicas, já o conjuntivo projetado para o epitélio é denominado de papilas dérmicas. O tecido conjuntivo frouxo da derme é nomeado de derme papilar, podendo ser visualizado logo abaixo do epitélio de revestimento. Em seguida, o tecido conjuntivo denso não-modelado da derme reticular, sendo este mais profundo e extenso.\r\n', 'HE', 40, 254, '', '', 0);
INSERT INTO `wp_laminas` (`ID`, `ID_materia`, `ID_assunto`, `nome`, `slug`, `descricao`, `coloracao`, `aumento`, `imagem`, `detalhes`, `mascaras`, `b_del`) VALUES
(102, 3, 10, '2.4 Tecido conjuntivo frouxo e denso não modelado', 'tecido_conjuntivo_frouxo_e_denso_2', 'Nesta imagem está em destaque a derme. O tecido conjuntivo frouxo da derme papilar é formado por delgadas fibras colágenas acidófilas, o que confere um aspecto homogêneo a este compartimento. O tecido frouxo apresenta todos os componentes típicos do conjuntivo, porém não há predominância de nenhum dos componentes. A derme reticular é caracterizada por apresentar feixes densos de fibras de colágeno, acidófilos e em diversas direções, sendo por isso classificado como conjuntivo denso não modelado. Uma predominância de feixes de colágeno é observada neste tecido quando comparado ao conjuntivo frouxo. Em ambos compartimentos da derme é possível perceber a presença de núcleos basófilos de células do tecido conjuntivo. O tecido conjuntivo é vascularizado, portanto podemos perceber a presença de vasos sanguíneos. Fibras elásticas e reticulares estão presentes na derme, no entanto, não é possível visualizá-las na coloração de HE. \r\n', 'HE', 100, 255, '', '', 0),
(103, 3, 10, '2.5 Tecido conjuntivo denso modelado', 'tecido_conjuntivo_denso_modelado', 'Fotomicrografia de tecido conjuntivo denso modelado. Este tecido apresenta feixes de fibras colágenas densas, os quais são acidófilos e paralelos. Esta organização se deve às forças de trações exercidas sobre o conjuntivo denso modelado. Notar os núcleos basófilos de fibroblastos que acompanham a organização paralela dos feixes. \r\n', 'HE', 100, 256, '', '', 0),
(104, 3, 10, '2.6 Fibroblasto no tecido conjuntivo denso não modelado', 'fibroblasto_no_tecido_conjuntivo', 'O fibroblasto é a célula do tecido conjuntivo propriamente dito presente em maior número. Quando esta célula está ativa, seu núcleo tem o formato ovoide. No entanto, o fibrobalsto de núcleo alongado, representa uma célula de baixa atividade, também nomeado fibrócito. Notar também a presença de fibras de colágeno acidófilas e de vasos sanguíneos.  \r\n', 'HE', 400, 257, '', '', 0),
(105, 3, 10, '2.7 Plasmócito no tecido conjuntivo frouxo', 'plasmocito_no_tecido_conjuntivo', 'Nesta imagem estão em destaque os plasmócitos. Estas células são originadas de linfócitos B diferenciados, aqueles que já tiveram contato com algum antígeno, sendo produtores e secretores de anticorpos. O plasmócito caracteriza-se por ser grande, ovoide e de núcleo excêntrico. O citoplasma desta célula é basófilo, devido a presença de retículo endoplasmático rugoso abundante. O plasmócito pode estar presente em qualquer tecido conjuntivo, porém é observado em maior número em áreas de inflamação, especialmente, inflamação crônica. \r\n', 'HE', 200, 258, '', '', 0),
(106, 3, 10, '2.8 Tecido adiposo unilocular', 'tecido_adiposo', 'Fotomicrografia de tecido adiposo unilocular, cujos adipócitos acumulam triglicérides na forma de gotícula única. Como a gordura é perdida durante o processamento histológico, estas células apresentam aspecto claro com uma pequena faixa de citoplasma corado na coloração de Hematoxilina Eosina. Os adipócitos uniloculares são células grandes e esféricas, porém devido a compressão exercida por outros adipócitos adjacentes, no tecido adiposo unilocular, esta célula apresenta formato poliédrico. O tecido adiposo é ricamente vascularizado e apresenta uma predominância de fibras reticulares, as quais oferecem um arcabouço para a organização celular. \r\n', 'HE', 100, 259, '', '', 0),
(107, 3, 10, '2.9 Tecido Adiposo unilocular', 'tecido_adiposo_unilocular', 'Nesta imagem está em destaque o adipócito unilocular, caracterizado por armazenar triglicerídeos na forma de gotícula única. A gordura é perdida durante processamento histológico, desta forma a célula está descorada na porção ocupada pela gotícula de gordura. O citoplasma do adipócito unilocular está compactado na periferia, determinando o aspecto de “anel de sinete”. O núcleo de aspecto achatado também pode ser percebido na periferia celular. ', 'HE', 200, 260, '', '', 0),
(108, 3, 12, 'Cartilagem hialina', 'cartilagem_hialina', 'Nesta fotomicrografia de cartilagem hialina é possível observar o tecido conjuntivo denso modelado do pericôndrio na periferia. Nesta região, notar que os feixes de fibras colágenas do tipo I são acidófilas e estão organizados em um único sentido. Na área mais externa do pericôndrio, denominada camada fibrosa externa, os núcleos basófilos correspondem aos fibroblastos. Porém, na região adjacente à cartilagem, camada celular, são encontradas células condrogênicas, as quais dão origem aos condroblastos. Estas células secretam os componentes da matriz cartilaginosa e ficam envolvidas por lacuna originando os condrócitos, o que determina o crescimento aposicional da cartilagem hialina. Os condrócitos, por sua vez, podem entrar em mitose e dar origem a outros condrócitos formando grupos isógenos, produzindo o crescimento intersticial deste tecido. Notar que na porção mais externa da cartilagem hialina os condrócitos apresentam-se ovoides, enquanto que na região mais central estas células assumem forma arredondada. A matriz da cartilagem hialina é formada por fibrilas de colágeno tipo II associadas ao ácido hialurônico, proteoglicanos e glicoproteínas adesivas. Como as fibrilas de colágeno tipo II são submicroscópicas, as mesmas não podem ser visualizadas em cortes histológicos para microscopia óptica. A presença de glicosaminoglicanos e proteoglicanos sulfatados na matriz, moléculas de caráter ácido, determina à afinidade à corante hematoxilina neste tecido. Desta forma, a matriz da cartilagem hialina tem aspecto homogêneo e basófilo. ', 'HE', 40, 261, '', '', 0),
(109, 3, 12, 'Cartilagem hialina ', 'cartilagem_hialina_2', 'Nesta imagem é possível verificar que a matriz extracelular da cartilagem hialina apresenta duas regiões: matriz territorial e interterritorial. A porção próxima as lacunas, denominada de matriz territorial, apresenta maior riqueza de glicosaminoglicanos (GAGs) e menor quantidade de fibrilas de colágeno tipo II, portanto são mais basófilas. Porém, o colágeno tipo II está mais abundante e os GAGs em menor quantidade na região entre as lacunas, sendo assim ocorre uma menor afinidade à corante Hematoxilina na matriz interterritorial. Notar ainda a presença de grupos isógenos.', 'HE', 200, 262, '', '', 0),
(110, 3, 12, 'Cartilagem elástica ', 'cartilagem_elastica', 'A cartilagem elástica apresenta características semelhantes a cartilagem hialina, porém na sua matriz ocorre a presença de fibras elásticas. Além disso, uma maior abundância de condrócitos é observada na cartilagem elástica. Nesta imagem, é possível perceber a transição do pericôndrio (presença de colágeno do tipo I acidófilo) para a cartilagem hialina (matriz homogênea e basófila). Por último, notar a presença de condrócitos ovoides na periferia que se tornam cada vez mais arredondas na porção mais interna. A cartilagem elástica pode ser encontrada no pavilhão da orelha, nas tubas auditivas interna e externa, laringe e epiglote.\r\n \r\n', 'HE', 100, 263, '', '', 0),
(111, 3, 12, 'Cartilagem elástica', 'cartilagem_elastica_2', 'Fotomicrografia da porção mais central da cartilagem elástica. Notam-se condrócitos arredondados envolvidos por matriz territorial a qual apresenta maior afinidade à corante hematoxilina. Quando comparado aos condrócitos da cartilagem hialina, estas células são maiores na cartilagem elástica, como visto nesta imagem. \r\n', 'HE', 200, 264, '', '', 0),
(112, 3, 12, 'Fibrocartilagem', 'fibrocartilagem', 'A fibrocartilagem, além de fibrilas de colágeno tipo II, caracteriza-se por apresentar uma grande abundância de fibras de colágeno tipo I, enquanto que os outros componentes da matriz extracelular (glicosaminoglicanos, proteoglicanos e glicoproteínas adesivas) estão em menores quantidades. Notar que os feixes de fibras colágenas acidófilos estão organizados na direção das forças das tensões funcionais. É possível observar a presença de lacunas com condrócitos. Neste tipo de cartilagem não é observado pericôndrio. Esta pode estar presente na sínfise púbica, discos intervertebrais, nos discos articulares e ligada ao osso. ', 'HE', 40, 265, '', '', 0),
(113, 3, 12, 'Fibrocartilagem', 'fibrocartilagem_2', 'Nesta fotomicrografia de ficrocartilagem nota-se que os condrócitos podem estar organizados em fileiras contínuas, na direção das forças de tração, assim como os feixes de fibras colágenas acidófilos. Próximo as lacunas, é possível perceber a presença de maior afinidade à corante hematoxilina, o que se deve a presença de substância fundamental em maior quantidade nesta região.', 'HE', 200, 266, '', '', 0),
(114, 3, 12, 'Osso cortical', 'osso_cortical', 'Corte de osso descalcificado em coloração de Hematoxilina e Eosina. Nesta imagem é possível observar, na periferia do tecido ósseo, a presença de periósteo. Este compartimento é formado por tecido conjuntivo e células osteoprogenitoras. Nota-se ainda o osso cortical maduro cujas lamelas de feixes de fibras colágenas acidófilas estão dispostas paralelas ou concêntricas, neste último caso, circundando o sistema de Harvers. Por último, observar osteócitos os quais acompanham a orientação das lamelas ósseas.\r\n', 'HE', 100, 267, '', '', 0),
(115, 3, 12, 'Osso cortical', 'osso_cortical_2', 'Nesta fotomicrografia destaca-se osso cortical maduro com sistema de Harvers e Volkmann. Os canais de Harvers são cilindros longos, sendo possível observá-los nesta imagem em corte transversal. Cada ósteon contém vaso e tecido nervoso, estando circundado por endósteo (não é possível visualizá-los neste corte histológico).  Nota-se que as lamelas formadas por feixes de fibras colágenas acidófilas apresentam organização concêntrica em torno dos canais de Havers.  O sistema de Volkmann estão organizados de forma transversal ou oblíqua em relação ao sistema de Harvers, sendo responsável por comunicar os ósteons entre si, com a medula e a superfície externa do osso. Percebe-se ainda, nesta fotomicrografia, a presença de lacunas de osteócitos as quais seguem as mesmas orientações das lamelas.\r\n', 'HE', 100, 268, '', '', 0),
(116, 3, 12, ' Osso esponjoso e medula óssea amarela ', 'osso_esponjoso_e_medula_ossea_amarela', 'Fotomicrografia de osso esponjoso com trabéculas ósseas e medula amarela. Pequena porção de osso compacto é visível, sendo possível caracterizá-lo, devido a presença de canal de Harvers. Em cada trabécula nota-se feixes de fibras colágenas acidófilos em organização lamelar de osso maduro e lacunas de osteócitos. Nos espaços medulares é visível medula amarela formada por tecido adiposo unilocular ricamente vascularizado. \r\n', 'HE', 40, 269, '', '', 0),
(117, 3, 13, ' Cérebro ', 'cerebro', 'Fotomicrografia de córtex cerebral formado por substância cinzenta. Nesta imagem notar as diferenças entre os núcleos de neurônios e células da glia. A porção do pericário do neurônio caracteriza-se por apresentar núcleo arredondado, grande e claro com nucléolo evidente. Enquanto que as células da neuroglia apresentam núcleos pequenos e bem corados. Nem sempre é possível definir os diferentes tipos de células da glia, porém algumas características particulares podem auxiliar na distinção. O oligodendrócito caracteriza-se por apresentar núcleo ovoide, bem corado, normalmente circundado por um halo branco. O astrócito apresenta núcleo menos corado e maior em comparação as outras células da glia. Por último, o núcleo da micróglia é achatado e bem corado. Ainda neste corte evidencia-se neurônio multipolar cujo citoplasma, na região do corpo celular, abriga abundante retículo endoplasmático rugoso. Tal organela é corada com hematoxilina, sendo possível perceber a presença de grumos basófilos os quais são denominados corpúsculos de Nissl. Os prolongamentos axonais amielínicas, juntamente com os dendritos e os prolongamentos das células gliais formam uma trama acidófila relativamente homogenia na substância cinzenta. Ainda é perceptível, nesta fotomicrografia, a presença de vasos sanguíneos.', 'HE', 400, 271, '', '', 0),
(118, 3, 13, 'Células ependimárias ', 'celulas_ependimarias', '\r\nNesta imagem de ventrículo cerebral é possível evidenciar substância branca e células ependimárias cúbicas ciliadas, as quais proporcionam o movimento do líquor. Na substância branca, nota-se os núcleos de células da glia, assim como prolongamentos gliais e axonais mielínicos, caracterizando uma trama acidófila de aspecto rendilhado neste compartimento, já que a mielina é perdida no processamento histológico.', 'HE', 200, 272, '', '', 0),
(119, 3, 13, 'Plexo coróide', 'plexo_coroide', '\r\nFotomicrografia de plexo coroide do cérebro. Tal compartimento é formado por pregas da pia máter de tecido conjuntivo frouxo e bem vascularizado, revestido por células ependimárias cúbicas modificadas (cílio está ausente). Este plexo é responsável pela produção do líquido cefalorraquidiano o qual protege e oferece nutrientes para o encéfalo e medula espinhal.', 'HE', 200, 273, '', '', 0),
(120, 3, 13, 'Cerebelo', 'cerebelo', 'Corte histológico no qual é possível evidenciar substância cinzenta e branca do cerebelo. No córtex cerebelar, da região mais externa para mais interna, notam-se três camadas: molecular, células de Purkinge e granulosa. Em seguida, observar a medula do cerebelo contendo substância branca com núcleos de células da glia e prolongamentos neuronais mielínicos. \r\n', 'HE', 40, 274, '', '', 0),
(121, 3, 13, 'Cerebelo', 'cerebelo_2', 'Nesta imagem está em destaque o córtex do cerebelo. A camada molecular é formada por corpos celulares de neurônios, axônios amielínicos, dendritos de células de Purkinje e núcleos de células gliais. Em seguida, a camada de células de Purkinje, caracterizada por conter células grandes com forma de frasco, dendritos longos e arborizados que se projetam para a camada molecular, enquanto que seus axônios mielínicos se projetam para a substância branca. Por último, a camada granulosa na qual se observam inúmeros pequenos neurônios, os menores do corpo. \r\n', 'HE', 400, 275, '', '', 0),
(122, 3, 13, 'Nervo periférico', 'nervo_periferico', 'Fotomicrografia de feixe nervoso periférico em corte longitudinal. É possível perceber o perineuro formado por várias camadas de células justapostas revestindo o feixe de fibras nervosas. O feixe nervoso apresenta trajeto ondulado contendo fibras nervosas acidófilas, não sendo possível diferenciá-las das fibras de colágeno tipo I do endoneuro. Os núcleos basófilos distribuídos no feixe nervoso correspondem a fibroblastos ou células de Schwann. \r\n', 'HE', 100, 276, '', '', 0),
(123, 3, 13, 'Nervo periférico', 'nervo_periferico_2', 'Nesta imagem de feixe nervoso em corte longitudinal é possível diferenciar o núcleo de fibroblastos (mais basófilo e achatado) do núcleo da célula de Schwann (mais ovoide). Ainda em destaque nesta fotomicrografia, nota-se fibra nervosa envolvida por bainha de mielina (bainha perdida durante processamento histológico ficando espaços descorados), com pontos de estrangulamento correspondentes a nódulos de ranvier. ', 'HE', 400, 277, '', '', 0),
(124, 3, 13, 'Nervo periférico', 'nervo_periferico_3', 'Corte histológico transversal de feixe nervoso periférico, com perineuro revestindo. As células de Schwann e fibroblastos são percebidas pela presença de núcleos basófilos. Neste tipo de corte é possível diferenciar a fibra nervosa mielínica da amielínica. Os pontos acidófilos irregulares representam as fibras nervosas amielínicas. Enquanto que a fibra nervosa mielínica caracteriza-se por ser um ponto acidófilo central circundando por uma área clara, correspondente à mielina. Ainda nesta estrutura, mais externamente, observa-se um anel acidófilo equivalente ao citoplasma da célula de Schwann.', 'HE', 200, 278, '', '[{"id":1,"post":null,"title":"","alias":"","description":"","color":"#bada55"}]', 0),
(125, 3, 13, 'Gânglio nervoso', 'ganglio_nervoso', 'Lâmina de pâncreas com gânglio nervoso. É possível verificar a presença de feixe de fibras nervosas saindo da estrutura ganglionar e revestimento de tecido conjuntivo. Os gânglios caracterizam-se por apresentar corpos celulares de neurônios agregados. ', 'HE', 40, 279, '', '', 0),
(126, 3, 13, 'Gânglio nervoso', 'ganglio_nervoso_2', 'Neste corte histológico está em destaque os corpos celulares de neurônios agregados de gânglio nervoso envolvido por tecido conjuntivo. As fibras nervosas acidófilas emaranhadas por entre os pericários formam feixe de fibras na periferia da estrutura. Por último, nota-se a presença de núcleos basófilos revestindo os pericários dos neurônios, denominadas células satélites. \r\n', 'HE', 200, 280, '', '', 0),
(127, 3, 14, 'Músculo estriado visceral', 'musculo_estriado_visceral', 'Lâmina de língua na qual se observa feixe de músculo estriado visceral em corte longitudinal e transversal. Notar a presença de perimísio de tecido conjuntivo envolvendo os feixes de fibras musculares estriadas.', 'HE', 100, 281, '', '', 0),
(128, 3, 14, 'Músculo estriado visceral', 'musculo_estriado_visceral_2', 'Fotomicrografia de tecido muscular estriado visceral em destaque. Cada fibra muscular estriada caracteriza-se por ser uma célula alongada, cilíndrica, não ramificada e multinucleada com seus núcleos alongados distribuídos na periferia. O citoplasma desta célula é acidófilo devido a presença de proteínas contráteis. Ainda neste corte se percebe a presença de estriações transversais, caracterizadas pela alternância de faixas claras e escuras, ocasionadas pela distribuição das miofibrilas.  Apesar de cada fibra ser circundada por endomísio o mesmo é formado especialmente por fibras reticulares, sendo assim não pode ser visualizado em colorações de Hematoxilina e Eosina.\r\n', 'HE', 400, 282, '', '', 0),
(129, 3, 14, 'Músculo estriado visceral', 'musculo_estriado_visceral_3', 'Corte histológico transversal de feixe de fibras musculares estriadas visceral. Cada fibra apresenta formato poliédrico com núcleo periférico e citoplasma acidófilo.\r\n', 'HE', 400, 283, '[]', '', 0),
(130, 3, 14, 'Músculo cardíaco', 'musculo_cardiaco', 'Lâmina de coração na qual se visualiza fibras musculares estriadas cardíacas em corte longitudinal. As fibras musculares cardíacas se dispõem em uma rede de células que formam anastomoses irregulares. Cada célula pode apresentar de um a dois núcleos ovoides e centralizados. O citoplasma da fibra é intensamente acidófilo, devido a presença de miofilamentos e inúmeras mitocôndrias, além disso é possível evidenciar estriações transversais. \r\n', 'HE', 200, 284, '', '', 0),
(131, 3, 14, 'Músculo cardíaco', 'musculo_cardiaco-2', 'Lâmina de músculo estriado cardíaco em corte longitudinal. Neste corte, além da presença de núcleo basófilo centralizado, fica também evidente o halo branco em torno desta estrutura que corresponde à acúmulo de glicogênio. Nesta fotomicrografia é possível identificar discos intercalares, complexo juncional que efetua adesão e comunicação entre as fibras cardíacas. Tecido conjuntivo frouxo pode ser percebido por entre as fibras do músculo cardíaco com abundante quantidade de capilares sanguíneos. ', 'HE', 400, 285, '', '', 0),
(132, 3, 14, 'Músculo cardíaco', 'musculo_cardiaco_3', 'Fotomicrografia de músculo estriado cardíaco em corte transversal. As fibras musculares cardíacas apresentam formato irregular com núcleo central e citoplasma acidófilo. Nota-se a presença de halo branco referente a glicogênio na região perinuclear. Por último, tecido conjuntivo entremeado entre as fibras musculares cardíacas com inúmeros capilares sanguíneos.', 'HE', 400, 286, '', '', 0),
(133, 3, 14, 'Músculo liso', 'musculo_liso', '\r\nLâmina de tecido muscular liso em corte longitudinal. Neste corte, as fibras musculares lisas aparecem com formato fusiforme, com núcleo basófilo único, central e alongado. O citoplasma celular é acidófilo, devido a presença de proteínas contráteis, porém estas moléculas não de organizam em miofibrilas, portanto não existe estriações transversais neste tipo de tecido muscular. ', 'HE', 200, 287, '', '', 0),
(135, 3, 14, 'Músculo liso', 'musculo_liso_2', 'Corte histológico de músculo liso em corte transversal. As fibras musculares lisas aparecem, neste corte, com formato circular ou poligonal. Quando é possível visualizar o núcleo, este se encontra centralizado.', 'HE', 200, 288, '', '', 0);
